import React, { useState } from 'react';

interface FilterOptions {
    priceRange: {
        min: number;
        max: number;
    };
    categories: string[];
    colors: string[];
    sizes: string[];
    discount: boolean;
    inStock: boolean;
}

interface FilterModalProps {
    isOpen: boolean;
    onClose: () => void;
    filters: FilterOptions;
    onApplyFilters: (filters: FilterOptions) => void;
    onClearFilters: () => void;
}

const FilterModal: React.FC<FilterModalProps> = ({
    isOpen,
    onClose,
    filters,
    onApplyFilters,
    onClearFilters,
}) => {
    const [localFilters, setLocalFilters] = useState<FilterOptions>(filters);

    const handlePriceChange = (key: 'min' | 'max', value: string) => {
        setLocalFilters(prev => ({
            ...prev,
            priceRange: {
                ...prev.priceRange,
                [key]: parseInt(value) || 0,
            },
        }));
    };

    const handleCategoryChange = (category: string) => {
        setLocalFilters(prev => ({
            ...prev,
            categories: prev.categories.includes(category)
                ? prev.categories.filter(c => c !== category)
                : [...prev.categories, category],
        }));
    };

    const handleColorChange = (color: string) => {
        setLocalFilters(prev => ({
            ...prev,
            colors: prev.colors.includes(color)
                ? prev.colors.filter(c => c !== color)
                : [...prev.colors, color],
        }));
    };

    const handleSizeChange = (size: string) => {
        setLocalFilters(prev => ({
            ...prev,
            sizes: prev.sizes.includes(size)
                ? prev.sizes.filter(s => s !== size)
                : [...prev.sizes, size],
        }));
    };

    const handleApply = () => {
        onApplyFilters(localFilters);
        onClose();
    };

    const handleClear = () => {
        const clearedFilters = {
            priceRange: { min: 0, max: 50000 },
            categories: [],
            colors: [],
            sizes: [],
            discount: false,
            inStock: false,
        };
        setLocalFilters(clearedFilters);
        onClearFilters();
    };

    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex justify-end">
            {/* Filter Sidebar */}
            <div className="bg-white w-full max-w-md h-full overflow-y-auto shadow-xl">
                {/* Header */}
                <div className="flex items-center justify-between p-4 border-b border-gray-200">
                    <h2 className="text-xl font-bold text-dark-900">Filters</h2>
                    <button
                        onClick={onClose}
                        className="p-2 hover:bg-gray-100 rounded-full transition-colors"
                    >
                        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                        </svg>
                    </button>
                </div>

                {/* Filter Content */}
                <div className="p-4 space-y-6">
                    {/* Price Range */}
                    <div>
                        <h3 className="font-semibold text-dark-900 mb-3">Price Range</h3>
                        <div className="grid grid-cols-2 gap-3">
                            <div>
                                <label className="block text-sm text-gray-600 mb-1">Min Price</label>
                                <input
                                    type="number"
                                    value={localFilters.priceRange.min}
                                    onChange={(e) => handlePriceChange('min', e.target.value)}
                                    className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-400"
                                    placeholder="0"
                                />
                            </div>
                            <div>
                                <label className="block text-sm text-gray-600 mb-1">Max Price</label>
                                <input
                                    type="number"
                                    value={localFilters.priceRange.max}
                                    onChange={(e) => handlePriceChange('max', e.target.value)}
                                    className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-400"
                                    placeholder="50000"
                                />
                            </div>
                        </div>
                    </div>

                    {/* Categories */}
                    <div>
                        <h3 className="font-semibold text-dark-900 mb-3">Categories</h3>
                        <div className="space-y-2">
                            {['Pashmina Shawls', 'Pashmina Stoles', 'Capes', 'Gift Hampers'].map((category) => (
                                <label key={category} className="flex items-center">
                                    <input
                                        type="checkbox"
                                        checked={localFilters.categories.includes(category)}
                                        onChange={() => handleCategoryChange(category)}
                                        className="w-4 h-4 text-primary-400 border-gray-300 rounded focus:ring-primary-400"
                                    />
                                    <span className="ml-2 text-sm text-gray-700">{category}</span>
                                </label>
                            ))}
                        </div>
                    </div>

                    {/* Colors */}
                    <div>
                        <h3 className="font-semibold text-dark-900 mb-3">Colors</h3>
                        <div className="grid grid-cols-4 gap-2">
                            {[
                                { name: 'Red', color: '#EF4444' },
                                { name: 'Blue', color: '#3B82F6' },
                                { name: 'Green', color: '#10B981' },
                                { name: 'Yellow', color: '#F59E0B' },
                                { name: 'Purple', color: '#8B5CF6' },
                                { name: 'Pink', color: '#EC4899' },
                                { name: 'Black', color: '#000000' },
                                { name: 'White', color: '#FFFFFF' },
                            ].map((colorOption) => (
                                <button
                                    key={colorOption.name}
                                    onClick={() => handleColorChange(colorOption.name)}
                                    className={`w-10 h-10 rounded-full border-2 ${localFilters.colors.includes(colorOption.name)
                                            ? 'border-primary-400 ring-2 ring-primary-200'
                                            : 'border-gray-300'
                                        }`}
                                    style={{ backgroundColor: colorOption.color }}
                                    title={colorOption.name}
                                />
                            ))}
                        </div>
                    </div>

                    {/* Sizes */}
                    <div>
                        <h3 className="font-semibold text-dark-900 mb-3">Sizes</h3>
                        <div className="flex flex-wrap gap-2">
                            {['S', 'M', 'L', 'XL', '2XL'].map((size) => (
                                <button
                                    key={size}
                                    onClick={() => handleSizeChange(size)}
                                    className={`px-4 py-2 border rounded-md text-sm font-medium transition-colors ${localFilters.sizes.includes(size)
                                            ? 'bg-primary-400 text-white border-primary-400'
                                            : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
                                        }`}
                                >
                                    {size}
                                </button>
                            ))}
                        </div>
                    </div>

                    {/* Other Filters */}
                    <div>
                        <h3 className="font-semibold text-dark-900 mb-3">Other Filters</h3>
                        <div className="space-y-3">
                            <label className="flex items-center">
                                <input
                                    type="checkbox"
                                    checked={localFilters.discount}
                                    onChange={(e) =>
                                        setLocalFilters(prev => ({ ...prev, discount: e.target.checked }))
                                    }
                                    className="w-4 h-4 text-primary-400 border-gray-300 rounded focus:ring-primary-400"
                                />
                                <span className="ml-2 text-sm text-gray-700">On Sale</span>
                            </label>
                            <label className="flex items-center">
                                <input
                                    type="checkbox"
                                    checked={localFilters.inStock}
                                    onChange={(e) =>
                                        setLocalFilters(prev => ({ ...prev, inStock: e.target.checked }))
                                    }
                                    className="w-4 h-4 text-primary-400 border-gray-300 rounded focus:ring-primary-400"
                                />
                                <span className="ml-2 text-sm text-gray-700">In Stock</span>
                            </label>
                        </div>
                    </div>
                </div>

                {/* Footer Buttons */}
                <div className="sticky bottom-0 bg-white border-t border-gray-200 p-4 space-y-3">
                    <button
                        onClick={handleApply}
                        className="w-full bg-primary-400 text-white py-3 px-4 rounded-md font-medium hover:bg-primary-500 transition-colors"
                    >
                        Apply Filters
                    </button>
                    <button
                        onClick={handleClear}
                        className="w-full bg-gray-100 text-gray-700 py-3 px-4 rounded-md font-medium hover:bg-gray-200 transition-colors"
                    >
                        Clear All
                    </button>
                </div>
            </div>
        </div>
    );
};

export default FilterModal;
