const API_BASE_URL = 'http://localhost:5001';

// Helper function to handle API responses
const handleResponse = async (response: Response) => {
    if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || `HTTP error! status: ${response.status}`);
    }
    return response.json();
};

// Generate a session ID for cart operations
const getSessionId = (): string => {
    let sessionId = localStorage.getItem('cart_session_id');
    if (!sessionId) {
        sessionId = 'session_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
        localStorage.setItem('cart_session_id', sessionId);
    }
    return sessionId;
};

// Product API
export const productAPI = {
    // Get all products
    getProducts: async () => {
        const response = await fetch(`${API_BASE_URL}/api/products`);
        return handleResponse(response);
    },

    // Get single product by ID
    getProduct: async (id: number) => {
        const response = await fetch(`${API_BASE_URL}/api/products/${id}`);
        return handleResponse(response);
    },

    // Get products by category
    getProductsByCategory: async (categorySlug: string) => {
        const response = await fetch(`${API_BASE_URL}/api/categories/${categorySlug}/products`);
        return handleResponse(response);
    }
};

// Category API
export const categoryAPI = {
    // Get all categories
    getCategories: async () => {
        const response = await fetch(`${API_BASE_URL}/api/categories`);
        return handleResponse(response);
    }
};

// Cart API
export const cartAPI = {
    // Get cart contents
    getCart: async () => {
        const sessionId = getSessionId();
        const response = await fetch(`${API_BASE_URL}/api/cart/${sessionId}`);
        return handleResponse(response);
    },

    // Add item to cart
    addToCart: async (productId: number, size?: string, quantity: number = 1) => {
        const sessionId = getSessionId();
        const response = await fetch(`${API_BASE_URL}/api/cart/${sessionId}/items`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ productId, size, quantity }),
        });
        return handleResponse(response);
    },

    // Update cart item quantity
    updateCartItem: async (itemId: number, quantity: number) => {
        const response = await fetch(`${API_BASE_URL}/api/cart/items/${itemId}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ quantity }),
        });
        return handleResponse(response);
    },

    // Remove cart item
    removeCartItem: async (itemId: number) => {
        const response = await fetch(`${API_BASE_URL}/api/cart/items/${itemId}`, {
            method: 'DELETE',
        });
        return handleResponse(response);
    },

    // Clear entire cart
    clearCart: async () => {
        const sessionId = getSessionId();
        const response = await fetch(`${API_BASE_URL}/api/cart/${sessionId}`, {
            method: 'DELETE',
        });
        return handleResponse(response);
    }
};

// Types for API responses
export interface Product {
  id: number;
  name: string;
  description: string;
  price: number;
  originalPrice: number;
  discount: number;
  rating: number;
  reviews: number;
  category: string;
  categorySlug: string;
  images: string[];
  sizes: string[];
  inStock: boolean;
  stockLeft: number;
  isFeatured: number;
  care?: string;
  returnPolicy?: string;
}

export interface Category {
    id: number;
    name: string;
    slug: string;
    description: string;
    image: string;
    productCount: number;
}

export interface CartItem {
    id: number;
    productId: number;
    name: string;
    size?: string;
    quantity: number;
    price: number;
    image: string;
}

export interface CartResponse {
    cartId: number;
    items: CartItem[];
    subtotal: number;
}
