import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';

// Product Detail Interface
interface ProductDetail {
    id: number;
    name: string;
    price: number;
    originalPrice: number;
    discount: number; // Percentage
    rating: number;
    reviews: number;
    sizes: string[];
    inStock: boolean;
    stockLeft?: number;
    images: string[];
    description: string;
    care: string;
    returnPolicy: string;
}

// Related Product Interface
interface RelatedProduct {
    id: number;
    name: string;
    price: number;
    originalPrice: number;
    discount: string;
    image: string;
}

// Mock product detail data - matching Figma design exactly
const productDetailData: { [key: number]: ProductDetail } = {
    1: {
        id: 1,
        name: 'Handwoven Pashmina multicolor shawl in dual tone',
        price: 4999,
        originalPrice: 8999,
        discount: 50,
        rating: 3.8,
        reviews: 42,
        sizes: ['S', 'M', 'L', 'XL', '2XL'],
        inStock: true,
        stockLeft: 3,
        images: [
            '/images/products/product-detail-1.png',
            '/images/products/product-detail-2.png',
            '/images/products/product-detail-3.png',
            '/images/products/product-detail-4.png',
            '/images/products/product-detail-5.png',
        ],
        description: `This elegant Handwoven Pashmina multicolor shawl in dual tone is a perfect blend of traditional charm and contemporary style.

Features:
• Made from high-quality pure crepe fabric
• Intricate embroidery and handwork details
• Perfect for weddings, festivals, and celebrations
• Comfortable fit with elegant drape
• Available in multiple sizes`,
        care: 'Dry clean only. Store in a cool, dry place. Avoid direct sunlight.',
        returnPolicy: '30-day easy returns and exchanges. Free return shipping.',
    },
};

// Related products
const relatedProducts: RelatedProduct[] = [
    {
        id: 2,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '50% Off',
        image: '/images/products/product-1.png',
    },
    {
        id: 3,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '50% Off',
        image: '/images/products/product-2.png',
    },
    {
        id: 4,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '50% Off',
        image: '/images/products/product-3.png',
    },
    {
        id: 5,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '50% Off',
        image: '/images/products/product-4.png',
    },
];

type TabType = 'description' | 'returns' | 'care' | 'reviews';

const ProductDetailPage: React.FC = () => {
    const { productId } = useParams<{ productId: string }>();
    const product = productDetailData[Number(productId)];

    const [selectedImageIndex, setSelectedImageIndex] = useState(0);
    const [selectedSize, setSelectedSize] = useState<string | null>(null);
    const [quantity, setQuantity] = useState(1);
    const [expandedTab, setExpandedTab] = useState<TabType | null>(null);

    if (!product) {
        return (
            <div className="min-h-screen bg-white flex items-center justify-center">
                <div className="text-center">
                    <h2 className="text-2xl font-bold text-gray-900 mb-4">Product not found</h2>
                    <Link to="/" className="text-blue-600 hover:underline">
                        Return to Home
                    </Link>
                </div>
            </div>
        );
    }

    const formatPrice = (price: number) => `₹${price.toLocaleString()}`;

    const handleQuantityChange = (type: 'increment' | 'decrement') => {
        if (type === 'increment') {
            setQuantity((prev) => prev + 1);
        } else {
            setQuantity((prev) => Math.max(1, prev - 1));
        }
    };

    const handleTabToggle = (tab: TabType) => {
        setExpandedTab(expandedTab === tab ? null : tab);
    };

    const renderStars = (rating: number) => {
        return (
            <div className="flex">
                {[1, 2, 3, 4, 5].map((star) => (
                    <svg
                        key={star}
                        className={`w-5 h-5 ${star <= Math.floor(rating) ? 'text-yellow-400' : 'text-gray-300'
                            }`}
                        fill="currentColor"
                        viewBox="0 0 20 20"
                    >
                        <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.538 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.783.57-1.838-.197-1.538-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.462a1 1 0 00.95-.69l1.07-3.292z" />
                    </svg>
                ))}
            </div>
        );
    };

    const renderTabContent = (tab: TabType) => {
        switch (tab) {
            case 'description':
                return (
                    <div className="py-4 text-gray-700 whitespace-pre-line text-base leading-relaxed">
                        {product.description}
                    </div>
                );
            case 'returns':
                return (
                    <div className="py-4 text-gray-700 text-base leading-relaxed">
                        {product.returnPolicy}
                    </div>
                );
            case 'care':
                return (
                    <div className="py-4 text-gray-700 text-base leading-relaxed">
                        {product.care}
                    </div>
                );
            case 'reviews':
                return (
                    <div className="py-4 text-gray-700 text-base leading-relaxed">
                        Customer reviews and ratings will be displayed here.
                    </div>
                );
            default:
                return null;
        }
    };

    return (
        <div className="bg-white min-h-screen">
            {/* Breadcrumb */}
            <div className="container mx-auto px-4 py-4">
                <nav className="text-sm text-gray-600">
                    <Link to="/" className="hover:underline">Home</Link> /
                    <Link to="/category/pashmina-shawls" className="hover:underline ml-1">Pashmina Shawls</Link> /
                    <span className="text-gray-900 font-medium ml-1">{product.name}</span>
                </nav>
            </div>

            {/* Main Product Section */}
            <div className="container mx-auto px-4 pb-16">
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
                    {/* Product Images */}
                    <div className="space-y-4">
                        {/* Main Image */}
                        <div className="aspect-square bg-gray-50 rounded-xl overflow-hidden">
                            <img
                                src={product.images[selectedImageIndex]}
                                alt={product.name}
                                className="w-full h-full object-cover"
                            />
                        </div>

                        {/* Thumbnail Images */}
                        <div className="flex gap-3 overflow-x-auto pb-2">
                            {product.images.map((image, index) => (
                                <button
                                    key={index}
                                    onClick={() => setSelectedImageIndex(index)}
                                    className={`flex-shrink-0 w-20 h-20 rounded-lg overflow-hidden border-2 transition-colors ${selectedImageIndex === index ? 'border-orange-500' : 'border-transparent hover:border-gray-300'
                                        }`}
                                >
                                    <img
                                        src={image}
                                        alt={`${product.name} ${index + 1}`}
                                        className="w-full h-full object-cover"
                                    />
                                </button>
                            ))}
                        </div>
                    </div>

                    {/* Product Details */}
                    <div className="space-y-6">
                        {/* Product Title */}
                        <h1 className="text-3xl font-bold text-black leading-tight">
                            {product.name}
                        </h1>

                        {/* Rating */}
                        <div className="flex items-center gap-3">
                            <span className="text-lg font-bold text-black">{product.rating}</span>
                            {renderStars(product.rating)}
                            <span className="text-sm text-gray-500">({product.reviews} ratings)</span>
                        </div>

                        {/* Pricing */}
                        <div className="space-y-2">
                            <div className="flex items-baseline gap-3">
                                <span className="text-3xl font-bold text-black">
                                    {formatPrice(product.price)}
                                </span>
                                <span className="text-xl text-gray-500 line-through">
                                    {formatPrice(product.originalPrice)}
                                </span>
                                <span className="bg-green-600 text-white px-3 py-1 rounded text-sm font-semibold">
                                    ( {product.discount}% Off )
                                </span>
                            </div>
                            <p className="text-sm text-gray-600">Inc. of all taxes</p>
                        </div>

                        {/* Stock Status */}
                        <div className="space-y-2">
                            <p className="text-base text-gray-700">Express Shipping Available</p>
                            <p className="text-base text-gray-700">In stock, ready to ship</p>
                            {product.stockLeft && product.stockLeft <= 3 && (
                                <p className="text-red-600 font-semibold text-sm">
                                    {product.stockLeft} Left
                                </p>
                            )}
                        </div>

                        {/* Size Selection */}
                        <div className="space-y-4">
                            <div className="flex items-center justify-between">
                                <h3 className="text-xl font-bold text-black">Select Size</h3>
                                <button className="text-red-500 text-sm font-semibold hover:underline">
                                    Size Guide
                                </button>
                            </div>

                            <div className="flex gap-3">
                                {product.sizes.map((size) => (
                                    <button
                                        key={size}
                                        onClick={() => setSelectedSize(size)}
                                        className={`w-12 h-12 rounded-full border-2 text-sm font-semibold transition-all ${selectedSize === size
                                                ? 'bg-black text-white border-black'
                                                : 'bg-white text-black border-gray-300 hover:border-black'
                                            }`}
                                    >
                                        {size}
                                    </button>
                                ))}
                            </div>
                        </div>

                        {/* Quantity Selector */}
                        <div className="space-y-3">
                            <h3 className="text-xl font-bold text-black">Quantity</h3>
                            <div className="flex items-center border border-gray-300 rounded-lg w-fit">
                                <button
                                    onClick={() => handleQuantityChange('decrement')}
                                    className="px-4 py-2 text-xl font-bold text-gray-700 hover:bg-gray-50"
                                >
                                    -
                                </button>
                                <span className="px-4 py-2 font-semibold border-l border-r border-gray-300">
                                    {quantity}
                                </span>
                                <button
                                    onClick={() => handleQuantityChange('increment')}
                                    className="px-4 py-2 text-xl font-bold text-gray-700 hover:bg-gray-50"
                                >
                                    +
                                </button>
                            </div>
                        </div>

                        {/* Action Buttons */}
                        <div className="space-y-4 pt-6">
                            <button
                                className="w-full text-white font-bold py-4 px-6 rounded-lg text-lg transition-colors"
                                style={{ backgroundColor: '#FF7500' }}
                                onMouseEnter={(e) => {
                                    e.currentTarget.style.backgroundColor = '#e66900';
                                }}
                                onMouseLeave={(e) => {
                                    e.currentTarget.style.backgroundColor = '#FF7500';
                                }}
                            >
                                Add to Bag
                            </button>

                            <button className="w-full border-2 border-black text-black py-4 px-6 rounded-lg font-bold text-lg hover:bg-black hover:text-white transition-all duration-200">
                                ♡ Add to Wishlist
                            </button>
                        </div>

                        {/* Additional Information */}
                        <div className="pt-6">
                            <button className="flex items-center gap-2 text-orange-500 text-xs font-semibold uppercase tracking-wider">
                                <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                                    <path d="M10 5a1 1 0 011 1v3h3a1 1 0 110 2h-3v3a1 1 0 11-2 0v-3H6a1 1 0 110-2h3V6a1 1 0 011-1z" />
                                </svg>
                                Add Additional Information
                            </button>
                        </div>
                    </div>
                </div>

                {/* Product Information Tabs */}
                <div className="mt-16 space-y-4">
                    {[
                        { key: 'description' as TabType, label: 'Description' },
                        { key: 'returns' as TabType, label: 'Return & Refund Policy' },
                        { key: 'care' as TabType, label: 'Care & Cleaning' },
                        { key: 'reviews' as TabType, label: 'Reviews' },
                    ].map(({ key, label }) => (
                        <div key={key} className="border-b border-gray-200">
                            <button
                                onClick={() => handleTabToggle(key)}
                                className="flex items-center justify-between w-full py-4 text-left font-bold text-xl text-black hover:text-gray-700 transition-colors"
                            >
                                {label}
                                <svg
                                    className={`w-5 h-5 transition-transform ${expandedTab === key ? 'rotate-180' : ''
                                        }`}
                                    fill="none"
                                    stroke="currentColor"
                                    viewBox="0 0 24 24"
                                >
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                                </svg>
                            </button>
                            {expandedTab === key && renderTabContent(key)}
                        </div>
                    ))}
                </div>

                {/* Related Products */}
                <div className="mt-16">
                    <h2 className="text-3xl font-bold text-black mb-8">You May Also Like</h2>
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                        {relatedProducts.map((relatedProduct) => (
                            <div key={relatedProduct.id} className="bg-white rounded-lg overflow-hidden shadow-sm hover:shadow-lg transition-shadow duration-300 group">
                                <Link to={`/product/${relatedProduct.id}`} className="relative aspect-square overflow-hidden block">
                                    <img
                                        src={relatedProduct.image}
                                        alt={relatedProduct.name}
                                        className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                                    />

                                    {/* Wishlist Heart */}
                                    <button
                                        onClick={(e) => {
                                            e.preventDefault();
                                            console.log('Toggle wishlist for product:', relatedProduct.id);
                                        }}
                                        className="absolute top-3 right-3 w-8 h-8 bg-white rounded-full flex items-center justify-center shadow-md hover:bg-gray-50 transition-colors"
                                    >
                                        <svg className="w-4 h-4 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
                                        </svg>
                                    </button>

                                    {/* Discount Badge */}
                                    <div className="absolute top-3 left-3 bg-green-600 text-white px-2 py-1 rounded text-sm font-medium">
                                        {relatedProduct.discount}
                                    </div>
                                </Link>

                                <div className="p-4">
                                    <Link to={`/product/${relatedProduct.id}`}>
                                        <h3 className="font-semibold text-black mb-2 line-clamp-2 hover:text-orange-500 transition-colors cursor-pointer">
                                            {relatedProduct.name}
                                        </h3>
                                    </Link>

                                    <div className="flex items-center justify-between mb-4">
                                        <div>
                                            <div className="flex items-center gap-2">
                                                <span className="text-lg font-bold text-black">
                                                    {formatPrice(relatedProduct.price)}
                                                </span>
                                                <span className="text-sm text-gray-500 line-through">
                                                    {formatPrice(relatedProduct.originalPrice)}
                                                </span>
                                            </div>
                                        </div>
                                    </div>

                                    <button className="w-full bg-white text-black font-semibold py-2 px-4 rounded border border-gray-300 hover:bg-gray-50 transition-colors text-sm">
                                        Add to Cart
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetailPage;
