import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';

// Mock product detail data
const productDetailData = {
    1: {
        id: 1,
        name: 'Hot Pink Pure Crepe Anarkali Suit',
        price: 5599,
        originalPrice: 8000,
        discount: 30,
        rating: 3.8,
        reviews: 20,
        colors: ['Pink', 'Red', 'Blue'],
        sizes: ['S', 'M', 'L', 'XL', '2XL'],
        inStock: true,
        stockLeft: 3,
        images: [
            '/images/products/product-detail-1.png',
            '/images/products/product-detail-2.png',
            '/images/products/product-detail-3.png',
            '/images/products/product-detail-4.png',
        ],
        description: `
      This elegant Hot Pink Pure Crepe Anarkali Suit is a perfect blend of traditional charm and contemporary style. 
      Crafted from premium pure crepe fabric, it offers comfort and sophistication for any special occasion.

      Features:
      • Made from high-quality pure crepe fabric
      • Intricate embroidery and handwork details
      • Perfect for weddings, festivals, and celebrations
      • Comfortable fit with elegant drape
      • Available in multiple sizes
    `,
        care: 'Dry clean only. Store in a cool, dry place. Avoid direct sunlight.',
        returnPolicy: '30-day easy returns and exchanges. Free return shipping.',
    },
};

// Related products
const relatedProducts = [
    {
        id: 2,
        name: 'Multicolor Shawl with handwork',
        price: 4999,
        originalPrice: 8999,
        discount: '50% Off',
        image: '/images/products/product-1.png',
    },
    {
        id: 3,
        name: 'Elegant Cape with Embroidery',
        price: 6999,
        originalPrice: 9999,
        discount: '30% Off',
        image: '/images/products/product-2.png',
    },
