import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { productAPI, cartAPI, Product, CartItem, CartResponse } from '../services/api';

// Product Detail Interface
interface ProductDetail {
    id: number;
    name: string;
    price: number;
    originalPrice: number;
    discount: number;
    rating: number;
    reviews: number;
    sizes: string[];
    inStock: boolean;
    stockLeft?: number;
    images: string[];
    description: string;
    care: string;
    returnPolicy: string;
}

// Related Product Interface
interface RelatedProduct {
    id: number;
    name: string;
    price: number;
    originalPrice: number;
    discount: string;
    image: string;
}

// Mock product detail data
const productDetailData: { [key: number]: ProductDetail } = {
    1: {
        id: 1,
        name: 'Handwoven Pashmina multicolor shawl in dual tone',
        price: 4999,
        originalPrice: 8999,
        discount: 50,
        rating: 3.0,
        reviews: 42,
        sizes: ['S', 'M', 'L', 'XL', '2XL'],
        inStock: true,
        stockLeft: 3,
        images: [
            '/images/products/product-1.png',
            '/images/products/product-2.png',
            '/images/products/product-3.png',
            '/images/products/product-4.png',
            '/images/products/product-1.png',
        ],
        description: 'Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus. Donec quam felis, ultricies nec, pellentesque eu, pretium quis, sem. Nulla consequat massa quis enim.',
        care: 'Dry clean only. Store in a cool, dry place. Avoid direct sunlight.',
        returnPolicy: '30-day easy returns and exchanges. Free return shipping.',
    },
};

// Related products
const relatedProducts: RelatedProduct[] = [
    {
        id: 2,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '( 50 % Off )',
        image: '/images/products/product-2.png',
    },
    {
        id: 3,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '( 50 % Off )',
        image: '/images/products/product-3.png',
    },
    {
        id: 4,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '( 50 % Off )',
        image: '/images/products/product-4.png',
    },
    {
        id: 5,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '( 50 % Off )',
        image: '/images/products/product-1.png',
    },
];

const ProductDetailPage: React.FC = () => {
    const { productId } = useParams<{ productId: string }>();
    const product = productDetailData[Number(productId)];

    const [selectedImage, setSelectedImage] = useState(product?.images[0] || '');
    const [selectedSize, setSelectedSize] = useState<string | null>(null);
    const [expandedSection, setExpandedSection] = useState<string | null>(null);
    const [isCartOpen, setIsCartOpen] = useState(false);
    const [cartItems, setCartItems] = useState<Array<{
        id: number;
        name: string;
        price: number;
        quantity: number;
        image: string;
    }>>([]);

    if (!product) {
        return <div className="container mx-auto py-16 text-center text-xl">Product not found.</div>;
    }

    const toggleSection = (section: string) => {
        setExpandedSection(expandedSection === section ? null : section);
    };

    const addToCart = () => {
        if (!product || !selectedSize) {
            alert('Please select a size first');
            return;
        }

        const existingItem = cartItems.find(item => item.id === product.id);
        if (existingItem) {
            setCartItems(cartItems.map(item =>
                item.id === product.id
                    ? { ...item, quantity: item.quantity + 1 }
                    : item
            ));
        } else {
            setCartItems([...cartItems, {
                id: product.id,
                name: product.name,
                price: product.price,
                quantity: 1,
                image: product.images[0]
            }]);
        }
        setIsCartOpen(true);
    };

    const updateQuantity = (id: number, change: number) => {
        setCartItems(cartItems.map(item =>
            item.id === id
                ? { ...item, quantity: Math.max(1, item.quantity + change) }
                : item
        ).filter(item => item.quantity > 0));
    };

    const getSubtotal = () => {
        return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
    };

    const renderStars = (rating: number) => {
        return (
            <div className="flex items-center">
                {[1, 2, 3, 4, 5].map((star) => (
                    <svg
                        key={star}
                        className={`w-4 h-4 ${star <= rating ? 'text-yellow-400' : 'text-gray-300'}`}
                        fill="currentColor"
                        viewBox="0 0 20 20"
                    >
