                                                        <button
                                                            onClick={() => updateQuantity(item.id, 1)}
                                                            className="px-3 py-1 text-white hover:opacity-80"
                                                            style={{
                                                                fontFamily: 'Quicksand',
                                                                fontWeight: 600,
                                                                fontSize: '20px'
                                                            }}
                                                        >
                                                            +
                                                        </button>
                                                    </div>

                                                    {/* Price */}
                                                    <span
                                                        style={{
                                                            color: '#252525',
                                                            fontFamily: 'Quicksand',
                                                            fontWeight: 500,
                                                            fontSize: '20px'
                                                        }}
