                                    <span
                                        style={{
                                            color: '#252525',
                                            fontFamily: 'Quicksand',
                                            fontWeight: 600,
                                            fontSize: '20px'
                                        }}
                                    >
                                        Subtotal
                                    </span>
                                    <span
                                        style={{
                                            color: '#252525',
                                            fontFamily: 'Quicksand',
                                            fontWeight: 500,
                                            fontSize: '20px'
