import React from 'react';

const HeroSection: React.FC = () => {
    return (
        <section
            className="relative min-h-screen flex items-center justify-center text-white"
            style={{
                backgroundImage: 'url("/images/banners/hero-background.png")',
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                backgroundRepeat: 'no-repeat'
            }}
        >
            {/* Dark overlay */}
            <div className="absolute inset-0 bg-black bg-opacity-40"></div>

            {/* Content */}
            <div className="relative z-10 container-custom text-center px-6">
                <div className="max-w-4xl mx-auto">
                    {/* Main Heading */}
                    <h1 className="text-4xl md:text-6xl font-bold mb-6 leading-tight">
                        "Picture-Perfect Moments,
                    </h1>

                    {/* Subheading */}
                    <h2 className="text-2xl md:text-3xl font-bold mb-4 text-primary-400">
                        "Thoughtfully Curated Gifts for Every Occasion"
                    </h2>

                    {/* Description */}
                    <p className="text-lg md:text-xl mb-8 max-w-2xl mx-auto leading-relaxed">
                        Explore our diverse range of gift options tailored for every special moment.
                    </p>

                    {/* CTA Button */}
                    <button className="btn-primary text-lg px-8 py-4 mb-16">
                        Shop Now
                    </button>

                    {/* Navigation Buttons */}
                    <div className="flex flex-wrap justify-center gap-4">
                        <button className="btn-secondary">
                            Our Products
                        </button>
                        <button className="btn-secondary">
                            Testimonial
                        </button>
                    </div>
                </div>
            </div>

            {/* Bottom scroll indicator */}
            <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce">
                <div className="w-6 h-10 border-2 border-white rounded-full flex justify-center">
                    <div className="w-1 h-3 bg-white rounded-full mt-2"></div>
                </div>
            </div>
        </section>
    );
};

export default HeroSection;
