import React from 'react';
import { Link } from 'react-router-dom';

const HeroSection = () => {
    const backgroundImages = [
        "/images/banners/hero1.png",
        "/images/banners/hero2.jpg",
        "/images/banners/hero3.jpg"
    ];

    return (
        <section className="relative min-h-[80vh] sm:min-h-screen flex items-center justify-center text-white overflow-hidden">
            {/* Background Images - Responsive layout */}
            <div className="absolute inset-0 flex flex-col sm:flex-row">
                {backgroundImages.map((image, index) => (
                    <div
                        key={index}
                        className="flex-1 relative"
                        style={{
                            backgroundImage: `url("${image}")`,
                            backgroundSize: 'cover',
                            backgroundPosition: 'center',
                            backgroundRepeat: 'no-repeat'
                        }}
                    />
                ))}
            </div>

            {/* Dark overlay */}
            <div className="absolute inset-0 bg-black bg-opacity-40"></div>

            {/* Content */}
            <div className="relative z-10 text-center px-4 sm:px-6 lg:px-8">
                <div className="max-w-4xl mx-auto">
                    {/* Main Heading - Mobile responsive */}
                    <h1 className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl xl:text-6xl font-bold mb-4 sm:mb-6 leading-tight">
                        "Picture-Perfect Moments,
                    </h1>

                    {/* Description - Mobile optimized */}
                    <p className="text-sm sm:text-base md:text-lg lg:text-xl mb-6 sm:mb-8 max-w-2xl mx-auto leading-relaxed px-2">
                        Explore our diverse range of gift options tailored for every special moment.
                    </p>

                    {/* CTA Button - Mobile friendly */}
                    <Link 
                        to="/listing" 
                        className="inline-block bg-primary-400 text-dark-900 font-semibold px-4 sm:px-6 py-2 sm:py-3 rounded-lg text-sm sm:text-base lg:text-lg hover:bg-primary-500 transition-colors duration-200 shadow-lg hover:shadow-xl"
                    >
                        Shop Now
                    </Link>
                </div>
            </div>
        </section>
    );
};

export default HeroSection;
