import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { productAPI, cartAPI, Product } from '../services/api';

const FeaturedProducts: React.FC = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchFeaturedProducts = async () => {
            try {
                const data = await productAPI.getProducts();
                // Filter only featured products
                const featuredProducts = data.filter((product: Product) => product.isFeatured === 1);
                setProducts(featuredProducts);
            } catch (error) {
                console.error('Error fetching featured products:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchFeaturedProducts();
    }, []);

    const handleAddToCart = async (productId: number) => {
        try {
            await cartAPI.addToCart(productId, undefined, 1);
            alert('Product added to cart!');
        } catch (error) {
            console.error('Error adding to cart:', error);
            alert('Failed to add product to cart');
        }
    };

    const formatPrice = (price: number) => {
        return `₹${price.toLocaleString()}`;
    };

    return (
        <section className="py-16 bg-gray-50">
            <div className="container-custom">
                {/* Section Header */}
                <div className="text-center mb-12">
                    <h2 className="text-3xl md:text-4xl font-bold text-dark-900 mb-8">
                        Featured Products
                    </h2>
                </div>

                {/* Products Grid */}
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                    {products.map((product) => (
                        <div
                            key={product.id}
                            className="bg-white rounded-lg overflow-hidden shadow-sm hover:shadow-lg transition-shadow duration-300 group"
                        >
                            {/* Product Image - Clickable */}
                            <Link to={`/product/${product.id}`} className="relative aspect-square overflow-hidden block">
                                <img
                                    src={product.image}
                                    alt={product.name}
                                    className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                                />

                                {/* Wishlist Heart */}
                                <button className="absolute top-3 right-3 w-8 h-8 bg-white rounded-full flex items-center justify-center shadow-md hover:bg-gray-50 transition-colors">
                                    <img
                                        src="/images/icons/heart-icon.svg"
                                        alt="Add to wishlist"
                                        className="w-4 h-4"
                                    />
                                </button>

                                {/* Discount Badge */}
                                {product.discount > 0 && (
                                    <div className="absolute top-3 left-3 bg-success-400 text-white px-2 py-1 rounded text-sm font-medium">
                                        ({product.discount}% Off)
                                    </div>
                                )}
                            </Link>

                            {/* Product Info */}
                            <div className="p-4">
                                {/* Product Name - Clickable */}
                                <Link to={`/product/${product.id}`}>
                                    <h3 className="font-semibold text-dark-900 mb-2 line-clamp-2 hover:text-primary-400 transition-colors cursor-pointer">
                                        {product.name}
                                    </h3>
                                </Link>

                                {/* Pricing */}
                                <div className="flex items-center justify-between mb-4">
                                    <div>
                                        <div className="flex items-center gap-2">
                                            <span className="text-lg font-bold text-dark-900">
                                                {formatPrice(product.price)}
                                            </span>
                                            {product.originalPrice > product.price && (
                                                <span className="text-sm text-gray-500 line-through">
                                                    {formatPrice(product.originalPrice)}
                                                </span>
                                            )}
                                        </div>
                                        {product.discount > 0 && (
                                            <span className="text-sm text-success-400 font-medium">
                                                ({product.discount}% Off)
                                            </span>
                                        )}
                                    </div>
                                </div>

                                {/* Add to Cart Button */}
                                <button className="w-full bg-dark-900 text-white py-2 px-4 rounded font-medium hover:bg-dark-800 transition-colors duration-200">
                                    Add to Cart
                                </button>
                            </div>
                        </div>
                    ))}
                </div>

                {/* View More Button */}
                <div className="text-center mt-12">
                    <button className="btn-secondary px-8 py-3">
                        View All Products
                    </button>
                </div>
            </div>
        </section>
    );
};

export default FeaturedProducts;
