import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { productAPI, cartAPI } from '../services/api';

const ProductDetailPage = () => {
    const { productId } = useParams();
    const [product, setProduct] = useState(null);
    const [relatedProducts, setRelatedProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [selectedImage, setSelectedImage] = useState(product?.images[0] || '');
    const [selectedSize, setSelectedSize] = useState(null);
    const [expandedSection, setExpandedSection] = useState(null);
    const [isCartOpen, setIsCartOpen] = useState(false);
    const [cartData, setCartData] = useState(null);

    // Fetch product data
    useEffect(() => {
        const fetchProductData = async () => {
            if (!productId) return;

            try {
                setLoading(true);
                setError(null);


                const productData = await productAPI.getProduct(Number(productId));
                setProduct(productData);
                setSelectedImage(productData.images[0] || '');


                const allProducts = await productAPI.getProducts();
                const related = allProducts.filter(p => p.id !== Number(productId)).slice(0, 4);
                setRelatedProducts(related);

            } catch (err) {
                setError(err instanceof Error ? err.message : 'Failed to load product');
            } finally {
                setLoading(false);
            }
        };

        fetchProductData();
    }, [productId]);

    // Fetch cart data
    useEffect(() => {
        const fetchCartData = async () => {
            try {
                const cart = await cartAPI.getCart();
                setCartData(cart);
            } catch (err) {
                // Cart fetch error handled gracefully
            }
        };

        fetchCartData();
    }, []);

    if (loading) {
        return (
            <div className="container mx-auto py-16 text-center">
                <div className="text-xl">Loading product...</div>
            </div>
        );
    }

    if (error || !product) {
        return (
            <div className="container mx-auto py-16 text-center">
                <div className="text-xl text-red-600">{error || 'Product not found'}</div>
            </div>
        );
    }

    const toggleSection = (section) => {
        setExpandedSection(expandedSection === section ? null : section);
    };

    const addToCart = async () => {
        if (!product || !selectedSize) {
            alert('Please select a size first');
            return;
        }

        try {
            await cartAPI.addToCart(product.id, selectedSize, 1);


            const updatedCart = await cartAPI.getCart();
            setCartData(updatedCart);
            setIsCartOpen(true);
        } catch (error) {

            alert('Failed to add item to cart');
        }
    };

    const updateQuantity = async (itemId, newQuantity) => {
        try {
            if (newQuantity < 1) {
                await cartAPI.removeCartItem(itemId);
            } else {
                await cartAPI.updateCartItem(itemId, newQuantity);
            }


            const updatedCart = await cartAPI.getCart();
            setCartData(updatedCart);
        } catch (error) {

            alert('Failed to update cart item');
        }
    };

    const addRelatedProductToCart = async (productId) => {
        try {
            await cartAPI.addToCart(productId, undefined, 1);


            const updatedCart = await cartAPI.getCart();
            setCartData(updatedCart);
            setIsCartOpen(true);
        } catch (error) {

            alert('Failed to add item to cart');
        }
    };

    const renderStars = (rating) => {
        return (
            <div className="flex items-center">
                {[1, 2, 3, 4, 5].map((star) => (
                    <svg
                        key={star}
                        className={`w-4 h-4 ${star <= rating ? 'text-yellow-400' : 'text-gray-300'}`}
                        fill="currentColor"
                        viewBox="0 0 20 20"
                    >
