import React, { useState, useEffect } from 'react';
import { useParams, useSearchParams, Link } from 'react-router-dom';
import { productAPI, cartAPI } from '../services/api';
import FilterModal from '../components/FilterModal';

const ListingPage = () => {
    const { categoryId } = useParams();

    const [searchParams] = useSearchParams();
    const [sortBy, setSortBy] = useState('best-match');
    const [allProducts, setAllProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [isFilterOpen, setIsFilterOpen] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage] = useState(16); // Show 16 products per page
    const [filters, setFilters] = useState({
        priceRange: { min: 0, max: 50000 },
        categories: [],
        colors: [],
        sizes: [],
        discount: false,
        inStock: false,
    });

    // Fetch products from API
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                setLoading(true);
                // Check both query params (?category=slug) and URL params (/category/:categoryId)
                const categorySlug = searchParams.get('category') || categoryId;

                let products;
                if (categorySlug) {
                    products = await productAPI.getProductsByCategory(categorySlug);
                } else {
                    products = await productAPI.getProducts();
                }

                // Add wishlist status (defaulting to false for now)
                const listingProducts = products.map(product => ({
                    ...product,
                    isWishlisted: false
                }));

                setAllProducts(listingProducts);
            } catch (error) {
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, [searchParams, categoryId]);

    // Reset to first page when products change
    useEffect(() => {
        setCurrentPage(1);
    }, [allProducts, filters, sortBy]);

    const handleAddToCart = async (productId) => {
        try {
            await cartAPI.addToCart(productId, undefined, 1);
            alert('Product added to cart!');
        } catch (error) {
            alert('Failed to add product to cart');
        }
    };

    const formatPrice = (price) => {
        return `₹${price.toLocaleString()}`;
    };

    // Filter and sort products
    const getFilteredProducts = () => {
        let filtered = [...allProducts];

        // Filter by price range
        filtered = filtered.filter(
            (product) => product.price >= filters.priceRange.min && product.price <= filters.priceRange.max
        );

        // Filter by categories
        if (filters.categories.length > 0) {
            filtered = filtered.filter((product) =>
                product.category && filters.categories.includes(product.category)
            );
        }

        // Filter by colors - Skip for now as colors aren't stored separately in database

        // Filter by sizes
        if (filters.sizes.length > 0) {
            filtered = filtered.filter((product) =>
                product.sizes && product.sizes.some((size) => filters.sizes.includes(size))
            );
        }

        // Filter by discount
        if (filters.discount) {
            filtered = filtered.filter((product) => product.discount > 0);
        }

        // Filter by stock
        if (filters.inStock) {
            filtered = filtered.filter((product) => product.stock_quantity > 0);
        }

        // Sort products
        switch (sortBy) {
            case 'price-low-high':
                filtered.sort((a, b) => a.price - b.price);
                break;
            case 'price-high-low':
                filtered.sort((a, b) => b.price - a.price);
                break;
            case 'newest':
                filtered.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));
                break;
            case 'rating':
                filtered.sort((a, b) => b.rating - a.rating);
                break;
            default:
                // Best match - keep original order
                break;
        }

        return filtered;
    };

    const filteredProducts = getFilteredProducts();
    const totalPages = Math.ceil(filteredProducts.length / itemsPerPage);
    const startIndex = (currentPage - 1) * itemsPerPage;
    const currentPageProducts = filteredProducts.slice(startIndex, startIndex + itemsPerPage);

    const getCategoryName = (categorySlug) => {
        const categoryMap = {
            'shawls-stoles': 'Shawls & Stoles',
            'capes-ponchos': 'Capes & Ponchos',
            'gift-hampers': 'Gift Hampers',
            'pashmina-shawls': 'Pashmina Shawls',
            'pashmina-stoles': 'Pashmina Stoles',
            'capes': 'Capes'
        };
        return categoryMap[categorySlug] || 'All Products';
    };

    const toggleWishlist = (productId) => {
        setAllProducts(prev => 
            prev.map(product => 
                product.id === productId 
                    ? { ...product, isWishlisted: !product.isWishlisted }
                    : product
            )
        );
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-gray-50 py-8">
                <div className="container mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 sm:gap-6 lg:gap-8">
                        {[...Array(8)].map((_, index) => (
                            <div key={index} className="bg-white rounded-lg overflow-hidden shadow-sm">
                                <div className="aspect-[3/4] bg-gray-200 animate-pulse"></div>
                                <div className="p-3 sm:p-4">
                                    <div className="h-3 sm:h-4 bg-gray-200 rounded animate-pulse mb-2"></div>
                                    <div className="h-3 sm:h-4 bg-gray-200 rounded animate-pulse mb-3 sm:mb-4 w-2/3"></div>
                                    <div className="h-8 sm:h-10 bg-gray-200 rounded animate-pulse"></div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-50">
            {/* Header Section */}
            <div className="bg-white border-b border-gray-200">
                <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-4 sm:py-6">
                    {/* Mobile Header */}
                    <div className="flex items-center justify-between lg:hidden mb-4">
                        <h1 className="text-xl sm:text-2xl font-bold text-gray-900">
                            {getCategoryName(searchParams.get('category') || categoryId)}
                        </h1>
                        <button
                            onClick={() => setIsFilterOpen(true)}
                            className="flex items-center gap-2 px-3 py-2 bg-dark-900 text-white rounded-lg text-sm font-medium"
                        >
                            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.207A1 1 0 013 6.5V4z" />
                            </svg>
                            Filters
                        </button>
                    </div>

                    {/* Desktop Header */}
                    <div className="hidden lg:flex items-center justify-between">
                        <h1 className="text-3xl font-bold text-gray-900">
                            {getCategoryName(searchParams.get('category') || categoryId)}
                        </h1>

                        {/* Sort and Filter Controls */}
                        <div className="flex items-center gap-4">
                            <select
                                value={sortBy}
                                onChange={(e) => setSortBy(e.target.value)}
                                className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-primary-400"
                            >
                                <option value="best-match">Best Match</option>
                                <option value="price-low-high">Price: Low to High</option>
                                <option value="price-high-low">Price: High to Low</option>
                                <option value="newest">Newest</option>
                                <option value="rating">Highest Rated</option>
                            </select>

                            <button
                                onClick={() => setIsFilterOpen(true)}
                                className="px-4 py-2 bg-dark-900 text-white rounded-lg text-sm font-medium hover:bg-dark-800 transition-colors"
                            >
                                Filters
                            </button>
                        </div>
                    </div>

                    {/* Mobile Sort Control */}
                    <div className="lg:hidden">
                        <select
                            value={sortBy}
                            onChange={(e) => setSortBy(e.target.value)}
                            className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-primary-400"
                        >
                            <option value="best-match">Best Match</option>
                            <option value="price-low-high">Price: Low to High</option>
                            <option value="price-high-low">Price: High to Low</option>
                            <option value="newest">Newest</option>
                            <option value="rating">Highest Rated</option>
                        </select>
                    </div>
                </div>
            </div>

            {/* Products Grid */}
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-6 sm:py-8">
                {currentPageProducts.length === 0 ? (
                    <div className="text-center py-12">
                        <h3 className="text-lg sm:text-xl font-medium text-gray-900 mb-2">No products found</h3>
                        <p className="text-gray-600 text-sm sm:text-base">Try adjusting your filters or search criteria.</p>
                    </div>
                ) : (
                    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 sm:gap-6 lg:gap-8">
                        {currentPageProducts.map((product) => (
                            <div
                                key={product.id}
                                className="group bg-white rounded-lg overflow-hidden shadow-sm hover:shadow-lg transition-shadow duration-300"
                            >
                                {/* Image */}
                                <Link
                                    to={`/product/${product.id}`}
                                    className="relative block aspect-[3/4] overflow-hidden"
                                >
                                    <img
                                        src={product.images[0] || "/images/products/placeholder.png"}
                                        alt={product.name}
                                        className="w-full h-full object-cover object-top transition-transform duration-300 group-hover:scale-105"
                                    />

                                    {/* Wishlist Button */}
                                    <button
                                        onClick={(e) => {
                                            e.preventDefault();
                                            toggleWishlist(product.id);
                                        }}
                                        className={`absolute top-2 sm:top-3 right-2 sm:right-3 w-7 h-7 sm:w-8 sm:h-8 rounded-full flex items-center justify-center shadow-md transition-colors ${
                                            product.isWishlisted 
                                                ? 'bg-red-500 text-white' 
                                                : 'bg-white text-gray-600 hover:bg-gray-50'
                                        }`}
                                        aria-label="Toggle wishlist"
                                    >
                                        <svg className="w-4 h-4 sm:w-5 sm:h-5" fill={product.isWishlisted ? "currentColor" : "none"} stroke="currentColor" viewBox="0 0 24 24">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
                                        </svg>
                                    </button>
                                </Link>

                                {/* Details */}
                                <div className="p-3 sm:p-4">
                                    <Link to={`/product/${product.id}`}>
                                        <h3 className="font-semibold text-gray-900 leading-snug text-sm sm:text-base line-clamp-2 mb-2 hover:text-primary-600 transition-colors">
                                            {product.name}
                                        </h3>
                                    </Link>

                                    <div className="mb-3 sm:mb-4">
                                        <div className="flex items-baseline gap-2 flex-wrap">
                                            {product.originalPrice > product.price && (
                                                <span className="text-gray-400 line-through text-sm sm:text-base">
                                                    {formatPrice(product.originalPrice)}
                                                </span>
                                            )}
                                            <span className="text-gray-900 font-semibold text-base sm:text-lg">
                                                {formatPrice(product.price)}
                                            </span>
                                            {product.discount > 0 && (
                                                <span className="text-green-600 font-semibold text-xs sm:text-sm">
                                                    ({product.discount}% Off)
                                                </span>
                                            )}
                                        </div>
                                    </div>

                                    {/* Add to Cart Button */}
                                    <button
                                        onClick={() => handleAddToCart(product.id)}
                                        className="w-full rounded-lg bg-dark-900 py-2 sm:py-3 text-center text-white text-sm sm:text-base font-semibold hover:bg-dark-800 transition-colors duration-200"
                                    >
                                        Add to Cart
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                )}

                {/* Pagination */}
                {totalPages > 1 && (
                    <div className="mt-8 sm:mt-12 flex justify-center">
                        <div className="flex items-center gap-2">
                            <button
                                onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                                disabled={currentPage === 1}
                                className="px-3 py-2 text-sm font-medium text-gray-500 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                Previous
                            </button>

                            {[...Array(totalPages)].map((_, index) => {
                                const page = index + 1;
                                const isCurrentPage = page === currentPage;

                                return (
                                    <button
                                        key={page}
                                        onClick={() => setCurrentPage(page)}
                                        className={`px-3 py-2 text-sm font-medium rounded-lg ${
                                            isCurrentPage
                                                ? 'bg-dark-900 text-white'
                                                : 'text-gray-500 bg-white border border-gray-300 hover:bg-gray-50'
                                        }`}
                                    >
                                        {page}
                                    </button>
                                );
                            })}

                            <button
                                onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                                disabled={currentPage === totalPages}
                                className="px-3 py-2 text-sm font-medium text-gray-500 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                Next
                            </button>
                        </div>
                    </div>
                )}
            </div>

            {/* Filter Modal */}
            <FilterModal
                isOpen={isFilterOpen}
                onClose={() => setIsFilterOpen(false)}
                filters={filters}
                setFilters={setFilters}
            />
        </div>
    );
};

export default ListingPage;
