import React, { useState, useEffect } from 'react';
import { useParams, useSearchParams, Link } from 'react-router-dom';
import { productAPI, cartAPI, Product } from '../services/api';
import FilterModal from '../components/FilterModal';

// Extended interface for wishlist
interface ListingProduct extends Product {
    isWishlisted: boolean;
}

interface FilterOptions {
    priceRange: {
        min: number;
        max: number;
    };
    categories: string[];
    colors: string[];
    sizes: string[];
    discount: boolean;
    inStock: boolean;
}

const ListingPage: React.FC = () => {
    const { categoryId } = useParams<{ categoryId: string }>();
    const [searchParams] = useSearchParams();
    const [sortBy, setSortBy] = useState('best-match');
    const [allProducts, setAllProducts] = useState<ListingProduct[]>([]);
    const [loading, setLoading] = useState(true);
    const [isFilterOpen, setIsFilterOpen] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage] = useState(12); // Show 12 products per page
    const [filters, setFilters] = useState<FilterOptions>({
        priceRange: { min: 0, max: 50000 },
        categories: [],
        colors: [],
        sizes: [],
        discount: false,
        inStock: false,
    });

    // Fetch products from API
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                setLoading(true);
                // Check both query params (?category=slug) and URL params (/category/:categoryId)
                const categorySlug = searchParams.get('category') || categoryId;

                let products: Product[];
                if (categorySlug) {
                    products = await productAPI.getProductsByCategory(categorySlug);
                } else {
                    products = await productAPI.getProducts();
                }

                // Add wishlist status (defaulting to false for now)
                const listingProducts: ListingProduct[] = products.map(product => ({
                    ...product,
                    isWishlisted: false
                }));

                setAllProducts(listingProducts);
            } catch (error) {
                // Error fetching products handled gracefully
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, [searchParams, categoryId]);

    const handleAddToCart = async (productId: number) => {
        try {
            await cartAPI.addToCart(productId, undefined, 1);
            alert('Product added to cart!');
        } catch (error) {
            alert('Failed to add product to cart');
        }
    };

    // Filter and sort products
    const getFilteredProducts = () => {
        let filtered = [...allProducts];

        // Filter by price range
        filtered = filtered.filter(
            (product) => product.price >= filters.priceRange.min && product.price <= filters.priceRange.max
        );

        // Filter by categories
        if (filters.categories.length > 0) {
            filtered = filtered.filter((product) =>
                product.category && filters.categories.includes(product.category)
            );
        }

        // Filter by colors - Skip for now as colors aren't stored separately in database
        // TODO: Implement color variants in database if needed

        // Filter by sizes
        if (filters.sizes.length > 0) {
            filtered = filtered.filter((product) =>
                product.sizes && product.sizes.some((size: string) => filters.sizes.includes(size))
            );
        }

        // Filter by discount
        if (filters.discount) {
            filtered = filtered.filter((product) => product.discount > 0);
        }

        // Filter by stock
        if (filters.inStock) {
            filtered = filtered.filter((product) => product.inStock);
        }

        // Sort products
        switch (sortBy) {
            case 'price-low':
                filtered.sort((a, b) => a.price - b.price);
                break;
            case 'price-high':
                filtered.sort((a, b) => b.price - a.price);
                break;
            case 'newest':
                filtered.sort((a, b) => b.id - a.id);
                break;
            case 'popularity':
                filtered.sort((a, b) => a.id - b.id);
                break;
            default:
                break;
        }

        return filtered;
    };

    const products = getFilteredProducts();

    // Get category name based on categoryId
    const getCategoryName = (id: string | undefined) => {
        switch (id) {
            case 'pashmina-shawls':
                return 'Pashmina Shawls';
            case 'pashmina-stoles':
                return 'Pashmina Stoles';
            case 'capes':
                return 'Capes';
            default:
                return 'Products';
        }
    };

    const toggleWishlist = (productId: number) => {
        // Wishlist functionality to be implemented
    };

    const handleApplyFilters = (newFilters: FilterOptions) => {
        setFilters(newFilters);
    };

    const handleClearFilters = () => {
        setFilters({
            priceRange: { min: 0, max: 50000 },
            categories: [],
            colors: [],
            sizes: [],
            discount: false,
            inStock: false,
        });
    };

    return (
        <div className="bg-white">
            {/* Category Header - Mobile Responsive */}
            <div className="container-custom py-4 sm:py-6">
                <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between mb-4 sm:mb-6 gap-4 px-4 sm:px-0">
                    <h1 className="text-xl sm:text-2xl font-bold text-dark-900">
                        {getCategoryName(categoryId)}
                    </h1>

                    <div className="flex gap-2 sm:gap-4 w-full sm:w-auto">
                        {/* Sort By Dropdown - Mobile responsive */}
                        <div className="relative flex-1 sm:flex-initial">
                            <select
                                value={sortBy}
                                onChange={(e) => setSortBy(e.target.value)}
                                className="appearance-none bg-white border border-dark-900 rounded-md px-3 sm:px-4 py-2 pr-6 sm:pr-8 text-xs sm:text-sm font-medium text-dark-900 focus:outline-none focus:ring-2 focus:ring-primary-400 w-full sm:w-auto"
                            >
                                <option value="best-match">Sort By</option>
                                <option value="price-low">Price: Low to High</option>
                                <option value="price-high">Price: High to Low</option>
                                <option value="newest">Newest First</option>
                                <option value="popularity">Most Popular</option>
                            </select>
                            <div className="absolute inset-y-0 right-0 flex items-center px-2 pointer-events-none">
                                <svg className="w-3 h-3 sm:w-4 sm:h-4 text-dark-900" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                                </svg>
                            </div>
                        </div>

                        {/* Filters Button - Mobile responsive */}
                        <button
                            onClick={() => setIsFilterOpen(true)}
                            className="flex items-center gap-1 sm:gap-2 bg-white border border-dark-900 rounded-md px-3 sm:px-4 py-2 text-xs sm:text-sm font-medium text-dark-900 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-primary-400 whitespace-nowrap"
                        >
                            <svg className="w-3 h-3 sm:w-4 sm:h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.207A1 1 0 013 6.5V4z" />
                            </svg>
                            <span className="hidden sm:inline">Filters</span>
                            {(filters.categories.length > 0 || filters.colors.length > 0 || filters.sizes.length > 0 || filters.discount || filters.inStock) && (
                                <span className="ml-0.5 sm:ml-1 bg-primary-400 text-white text-xs px-1.5 sm:px-2 py-0.5 rounded-full">
                                    {filters.categories.length + filters.colors.length + filters.sizes.length + (filters.discount ? 1 : 0) + (filters.inStock ? 1 : 0)}
                                </span>
                            )}
                        </button>
                    </div>
                </div>

                {/* Products Grid - Mobile optimized */}
                <div className="grid grid-cols-2 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3 sm:gap-4 md:gap-6 px-4 sm:px-0">
                    {loading ? (
                        // Loading skeleton
                        [...Array(8)].map((_, index) => (
                            <div key={index} className="group cursor-pointer">
                                <div className="relative bg-gray-200 rounded-lg overflow-hidden mb-4 h-64 animate-pulse"></div>
                                <div className="space-y-2">
                                    <div className="h-4 bg-gray-200 rounded animate-pulse"></div>
                                    <div className="h-4 bg-gray-200 rounded animate-pulse w-2/3"></div>
                                    <div className="h-10 bg-gray-200 rounded animate-pulse mt-3"></div>
                                </div>
                            </div>
                        ))
                    ) : products.length === 0 ? (
                        <div className="col-span-full text-center py-12">
                            <p className="text-gray-500 text-lg">No products found matching your criteria.</p>
                        </div>
                    ) : (
                        products.map((product) => (
                            <div key={product.id} className="group cursor-pointer">
                                {/* Product Image Container - Mobile responsive */}
                                <Link to={`/product/${product.id}`} className="relative bg-gray-100 rounded-lg overflow-hidden mb-2 sm:mb-3 md:mb-4 block">
                                    <img
                                        src={product.images[0] || '/images/products/placeholder.png'}
                                        alt={product.name}
                                        className="w-full aspect-square sm:h-64 object-cover group-hover:scale-105 transition-transform duration-300"
                                    />

                                    {/* Wishlist Button - Mobile responsive */}
                                    <button
                                        onClick={(e) => {
                                            e.stopPropagation();
                                            toggleWishlist(product.id);
                                        }}
                                        className="absolute top-2 sm:top-3 right-2 sm:right-3 p-1.5 sm:p-2 bg-white rounded-full shadow-md hover:bg-gray-50 transition-colors"
                                    >
                                        <svg
                                            className={`w-4 h-4 sm:w-5 sm:h-5 ${product.isWishlisted ? 'text-red-500 fill-current' : 'text-gray-400'}`}
                                            fill="none"
                                            stroke="currentColor"
                                            viewBox="0 0 24 24"
                                        >
                                            <path
                                                strokeLinecap="round"
                                                strokeLinejoin="round"
                                                strokeWidth={2}
                                                d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
                                            />
                                        </svg>
                                    </button>

                                    {/* Rating - Mobile responsive */}
                                    <div className="absolute bottom-2 sm:bottom-3 left-2 sm:left-3 flex items-center gap-1 bg-white rounded-full px-1.5 sm:px-2 py-0.5 sm:py-1 text-xs">
                                        <span className="text-yellow-500">★</span>
                                        <span className="text-gray-600">4.5</span>
                                    </div>
                                </Link>

                                {/* Product Info - Mobile responsive */}
                                <div className="space-y-1 sm:space-y-2">
                                    <Link to={`/product/${product.id}`}>
                                        <h3 className="text-xs sm:text-sm font-medium text-dark-900 line-clamp-2 hover:text-primary-400 transition-colors cursor-pointer">
                                            {product.name}
                                        </h3>
                                    </Link>

                                    <div className="flex flex-col sm:flex-row sm:items-center gap-1 sm:gap-2">
                                        <span className="text-sm sm:text-lg font-bold text-dark-900">
                                            ₹{product.price.toLocaleString()}
                                        </span>
                                        <div className="flex items-center gap-1 sm:gap-2">
                                            <span className="text-xs sm:text-sm text-gray-500 line-through">
                                                ₹{product.originalPrice.toLocaleString()}
                                            </span>
                                            <span className="text-xs font-medium text-success-400 bg-green-100 px-1.5 sm:px-2 py-0.5 rounded">
                                                {product.discount}% Off
                                            </span>
                                        </div>
                                    </div>

                                    {/* Add to Cart Button - Mobile responsive */}
                                    <button
                                        onClick={() => handleAddToCart(product.id)}
                                        className="w-full bg-primary-400 text-white py-1.5 sm:py-2 px-2 sm:px-4 rounded-md font-medium hover:bg-primary-500 transition-colors mt-2 sm:mt-3 text-xs sm:text-sm"
                                    >
                                        Add to Cart
                                    </button>
                                </div>
                            </div>
                        ))
                    )}
                </div>

                {/* Pagination - Mobile responsive */}
                <div className="flex justify-center items-center mt-8 sm:mt-10 md:mt-12 gap-1 sm:gap-2 px-4 sm:px-0">
                    <button className="p-1.5 sm:p-2 rounded-md border border-gray-300 hover:bg-gray-50 disabled:opacity-50" disabled>
                        <svg className="w-4 h-4 sm:w-5 sm:h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                        </svg>
                    </button>

                    <button className="px-2.5 sm:px-3 py-1.5 sm:py-2 bg-primary-400 text-white rounded-md font-medium text-sm">1</button>
                    <button className="px-2.5 sm:px-3 py-1.5 sm:py-2 hover:bg-gray-50 rounded-md font-medium text-sm">2</button>
                    <button className="px-2.5 sm:px-3 py-1.5 sm:py-2 hover:bg-gray-50 rounded-md font-medium text-sm">3</button>
                    <span className="px-1 sm:px-2 text-sm">...</span>
                    <button className="px-2.5 sm:px-3 py-1.5 sm:py-2 hover:bg-gray-50 rounded-md font-medium text-sm">10</button>

                    <button className="p-1.5 sm:p-2 rounded-md border border-gray-300 hover:bg-gray-50">
                        <svg className="w-4 h-4 sm:w-5 sm:h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                        </svg>
                    </button>
                </div>

                {/* Filter Modal */}
                <FilterModal
                    isOpen={isFilterOpen}
                    onClose={() => setIsFilterOpen(false)}
                    filters={filters}
                    onApplyFilters={handleApplyFilters}
                    onClearFilters={handleClearFilters}
                />
            </div>
        </div>
    );
};

export default ListingPage;
