import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import FilterModal from '../components/FilterModal';

// Product interface
interface Product {
  id: number;
  name: string;
  price: number;
  originalPrice: number;
  discount: string;
  image: string;
  isWishlisted: boolean;
  category: string;
  colors?: string[];
  sizes?: string[];
  inStock: boolean;
}

// Mock product data for listing page
const listingProducts: Product[] = [
  {
    id: 1,
    name: 'Multicolor Shawl with handwork',
    price: 4999,
    originalPrice: 8999,
    discount: '50% Off',
    image: '/images/products/product-1.png',
    isWishlisted: false,
    category: 'Pashmina Shawls',
    colors: ['Red', 'Blue'],
    sizes: ['M', 'L', 'XL'],
    inStock: true,
  },
    {
        id: 2,
        name: 'Multicolor Shawl with handwork',
        price: 4999,
        originalPrice: 8999,
        discount: '50% Off',
        image: '/images/products/product-2.png',
        isWishlisted: false,
        category: 'Pashmina Stoles',
        colors: ['Green', 'Yellow'],
        sizes: ['S', 'M', 'L'],
        inStock: true,
    },
    {
        id: 3,
        name: 'Elegant Cape with Embroidery',
        price: 6999,
        originalPrice: 9999,
        discount: '30% Off',
        image: '/images/products/product-3.png',
        isWishlisted: false,
        category: 'Capes',
        colors: ['Purple', 'Pink'],
        sizes: ['M', 'L', 'XL', '2XL'],
        inStock: true,
    },
    {
        id: 4,
        name: 'Premium Gift Hamper',
        price: 7999,
        originalPrice: 10999,
        discount: '25% Off',
        image: '/images/products/product-4.png',
        isWishlisted: false,
        category: 'Gift Hampers',
        colors: ['Multi'],
        sizes: ['One Size'],
        inStock: false,
    },
    {
        id: 5,
        name: 'Luxury Pashmina Stole',
        price: 3999,
        originalPrice: 7999,
        discount: '50% Off',
        image: '/images/products/product-5.png',
        isWishlisted: false,
        category: 'Pashmina Stoles',
        colors: ['Black', 'White'],
        sizes: ['S', 'M', 'L'],
        inStock: true,
    },
    {
        id: 6,
        name: 'Traditional Shawl Collection',
        price: 5499,
        originalPrice: 8999,
        discount: '40% Off',
        image: '/images/products/product-6.png',
        isWishlisted: false,
        category: 'Pashmina Shawls',
        colors: ['Red', 'Yellow'],
        sizes: ['M', 'L'],
        inStock: true,
    },
    {
        id: 7,
        name: 'Designer Cape - Limited Edition',
        price: 8999,
        originalPrice: 12999,
        discount: '30% Off',
        image: '/images/products/product-1.png',
        isWishlisted: false,
        category: 'Capes',
        colors: ['Blue', 'Green'],
        sizes: ['L', 'XL'],
        inStock: true,
    },
    {
        id: 8,
        name: 'Premium Stole - Handwoven',
        price: 4499,
        originalPrice: 6999,
        discount: '35% Off',
        image: '/images/products/product-2.png',
        isWishlisted: false,
        category: 'Pashmina Stoles',
        colors: ['Purple', 'Pink'],
        sizes: ['S', 'M', 'L', 'XL'],
        inStock: true,
    },
];

interface FilterOptions {
    priceRange: {
        min: number;
        max: number;
    };
    categories: string[];
    colors: string[];
    sizes: string[];
    discount: boolean;
    inStock: boolean;
}

const ListingPage: React.FC = () => {
    const { categoryId } = useParams<{ categoryId: string }>();
    const [sortBy, setSortBy] = useState('best-match');
    const [allProducts] = useState(listingProducts);
    const [isFilterOpen, setIsFilterOpen] = useState(false);
    const [filters, setFilters] = useState<FilterOptions>({
        priceRange: { min: 0, max: 50000 },
        categories: [],
        colors: [],
        sizes: [],
        discount: false,
        inStock: false,
    });

    // Filter and sort products
    const getFilteredProducts = () => {
        let filtered = [...allProducts];

        // Filter by price range
        filtered = filtered.filter(
            (product) => product.price >= filters.priceRange.min && product.price <= filters.priceRange.max
        );

            // Filter by categories
    if (filters.categories.length > 0) {
      filtered = filtered.filter((product) => 
        product.category && filters.categories.includes(product.category)
      );
    }

    // Filter by colors
    if (filters.colors.length > 0) {
      filtered = filtered.filter((product) =>
        product.colors && product.colors.some((color) => filters.colors.includes(color))
      );
    }

    // Filter by sizes
    if (filters.sizes.length > 0) {
      filtered = filtered.filter((product) =>
        product.sizes && product.sizes.some((size) => filters.sizes.includes(size))
      );
    }

        // Filter by discount
        if (filters.discount) {
            filtered = filtered.filter((product) => product.discount && product.discount !== '0% Off');
        }

        // Filter by stock
        if (filters.inStock) {
            filtered = filtered.filter((product) => product.inStock);
        }

        // Sort products
        switch (sortBy) {
            case 'price-low':
                filtered.sort((a, b) => a.price - b.price);
                break;
            case 'price-high':
                filtered.sort((a, b) => b.price - a.price);
                break;
            case 'newest':
                filtered.sort((a, b) => b.id - a.id);
                break;
            case 'popularity':
                filtered.sort((a, b) => a.id - b.id);
                break;
            default:
                break;
        }

        return filtered;
    };

    const products = getFilteredProducts();

    // Get category name based on categoryId
    const getCategoryName = (id: string | undefined) => {
        switch (id) {
            case 'pashmina-shawls':
                return 'Pashmina Shawls';
            case 'pashmina-stoles':
                return 'Pashmina Stoles';
            case 'capes':
                return 'Capes';
            default:
                return 'Products';
        }
    };

    const toggleWishlist = (productId: number) => {
        // In a real app, this would update the wishlist state
        console.log('Toggle wishlist for product:', productId);
    };

    const handleApplyFilters = (newFilters: FilterOptions) => {
        setFilters(newFilters);
    };

    const handleClearFilters = () => {
        setFilters({
            priceRange: { min: 0, max: 50000 },
            categories: [],
            colors: [],
            sizes: [],
            discount: false,
            inStock: false,
        });
    };

    return (
        <div className="bg-white">
            {/* Category Header */}
            <div className="container-custom py-6">
                <div className="flex items-center justify-between mb-6">
                    <h1 className="text-2xl font-bold text-dark-900">
                        {getCategoryName(categoryId)}
                    </h1>

                    <div className="flex gap-4">
                        {/* Sort By Dropdown */}
                        <div className="relative">
                            <select
                                value={sortBy}
                                onChange={(e) => setSortBy(e.target.value)}
                                className="appearance-none bg-white border border-dark-900 rounded-md px-4 py-2 pr-8 text-sm font-medium text-dark-900 focus:outline-none focus:ring-2 focus:ring-primary-400"
                            >
                                <option value="best-match">Sort By</option>
                                <option value="price-low">Price: Low to High</option>
                                <option value="price-high">Price: High to Low</option>
                                <option value="newest">Newest First</option>
                                <option value="popularity">Most Popular</option>
                            </select>
                            <div className="absolute inset-y-0 right-0 flex items-center px-2 pointer-events-none">
                                <svg className="w-4 h-4 text-dark-900" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                                </svg>
                            </div>
                        </div>

                        {/* Filters Button */}
                        <button
                            onClick={() => setIsFilterOpen(true)}
                            className="flex items-center gap-2 bg-white border border-dark-900 rounded-md px-4 py-2 text-sm font-medium text-dark-900 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-primary-400"
                        >
                            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.207A1 1 0 013 6.5V4z" />
                            </svg>
                            Filters
                            {(filters.categories.length > 0 || filters.colors.length > 0 || filters.sizes.length > 0 || filters.discount || filters.inStock) && (
                                <span className="ml-1 bg-primary-400 text-white text-xs px-2 py-0.5 rounded-full">
                                    {filters.categories.length + filters.colors.length + filters.sizes.length + (filters.discount ? 1 : 0) + (filters.inStock ? 1 : 0)}
                                </span>
                            )}
                        </button>
                    </div>
                </div>

                {/* Products Grid */}
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                    {products.map((product) => (
                        <div key={product.id} className="group cursor-pointer">
                            {/* Product Image Container */}
                            <div className="relative bg-gray-100 rounded-lg overflow-hidden mb-4">
                                <img
                                    src={product.image}
                                    alt={product.name}
                                    className="w-full h-64 object-cover group-hover:scale-105 transition-transform duration-300"
                                />

                                {/* Wishlist Button */}
                                <button
                                    onClick={(e) => {
                                        e.stopPropagation();
                                        toggleWishlist(product.id);
                                    }}
                                    className="absolute top-3 right-3 p-2 bg-white rounded-full shadow-md hover:bg-gray-50 transition-colors"
                                >
                                    <svg
                                        className={`w-5 h-5 ${product.isWishlisted ? 'text-red-500 fill-current' : 'text-gray-400'}`}
                                        fill="none"
                                        stroke="currentColor"
                                        viewBox="0 0 24 24"
                                    >
                                        <path
                                            strokeLinecap="round"
                                            strokeLinejoin="round"
                                            strokeWidth={2}
                                            d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
                                        />
                                    </svg>
                                </button>

                                {/* Rating */}
                                <div className="absolute bottom-3 left-3 flex items-center gap-1 bg-white rounded-full px-2 py-1 text-xs">
                                    <span className="text-yellow-500">★</span>
                                    <span className="text-gray-600">4.5</span>
                                </div>
                            </div>

                            {/* Product Info */}
                            <div className="space-y-2">
                                <h3 className="text-sm font-medium text-dark-900 line-clamp-2">
                                    {product.name}
                                </h3>

                                <div className="flex items-center gap-2">
                                    <span className="text-lg font-bold text-dark-900">
                                        ₹{product.price.toLocaleString()}
                                    </span>
                                    <span className="text-sm text-gray-500 line-through">
                                        ₹{product.originalPrice.toLocaleString()}
                                    </span>
                                    <span className="text-xs font-medium text-success-400 bg-green-100 px-2 py-0.5 rounded">
                                        {product.discount}
                                    </span>
                                </div>

                                {/* Add to Cart Button */}
                                <button className="w-full bg-primary-400 text-white py-2 px-4 rounded-md font-medium hover:bg-primary-500 transition-colors mt-3">
                                    Add to Cart
                                </button>
                            </div>
                        </div>
                    ))}
                </div>

                {/* Pagination */}
                <div className="flex justify-center items-center mt-12 gap-2">
                    <button className="p-2 rounded-md border border-gray-300 hover:bg-gray-50 disabled:opacity-50" disabled>
                        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                        </svg>
                    </button>

                    <button className="px-3 py-2 bg-primary-400 text-white rounded-md font-medium">1</button>
                    <button className="px-3 py-2 hover:bg-gray-50 rounded-md font-medium">2</button>
                    <button className="px-3 py-2 hover:bg-gray-50 rounded-md font-medium">3</button>
                    <span className="px-2">...</span>
                    <button className="px-3 py-2 hover:bg-gray-50 rounded-md font-medium">10</button>

                    <button className="p-2 rounded-md border border-gray-300 hover:bg-gray-50">
                        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                        </svg>
                    </button>
                </div>

                {/* Filter Modal */}
                <FilterModal
                    isOpen={isFilterOpen}
                    onClose={() => setIsFilterOpen(false)}
                    filters={filters}
                    onApplyFilters={handleApplyFilters}
                    onClearFilters={handleClearFilters}
                />
            </div>
        </div>
    );
};

export default ListingPage;
