import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';

// Product Detail Interface
interface ProductDetail {
    id: number;
    name: string;
    price: number;
    originalPrice: number;
    discount: number; // Percentage
    rating: number;
    reviews: number;
    sizes: string[];
    inStock: boolean;
    stockLeft?: number;
    images: string[];
    description: string;
    care: string;
    returnPolicy: string;
}

// Related Product Interface
interface RelatedProduct {
    id: number;
    name: string;
    price: number;
    originalPrice: number;
    discount: string;
    image: string;
}

// Mock product detail data - matching Figma design exactly
const productDetailData: { [key: number]: ProductDetail } = {
    1: {
        id: 1,
        name: 'Handwoven Pashmina multicolor shawl in dual tone',
        price: 4999,
        originalPrice: 8999,
        discount: 50,
        rating: 3.8,
        reviews: 42,
        sizes: ['S', 'M', 'L', 'XL', '2XL'],
        inStock: true,
        stockLeft: 3,
        images: [
            '/images/products/product-1.png',
            '/images/products/product-2.png',
            '/images/products/product-3.png',
            '/images/products/product-4.png',
            '/images/products/product-1.png',
        ],
        description: 'Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus. Donec quam felis, ultricies nec, pellentesque eu, pretium quis, sem. Nulla consequat massa quis enim.',
        care: 'Dry clean only. Store in a cool, dry place. Avoid direct sunlight.',
        returnPolicy: '30-day easy returns and exchanges. Free return shipping.',
    },
};

// Related products data
const relatedProducts: RelatedProduct[] = [
    {
        id: 2,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '50% Off',
        image: '/images/products/product-2.png',
    },
    {
        id: 3,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '50% Off',
        image: '/images/products/product-3.png',
    },
    {
        id: 4,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '50% Off',
        image: '/images/products/product-4.png',
    },
    {
        id: 5,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '50% Off',
        image: '/images/products/product-1.png',
    },
];

const ProductDetailPage: React.FC = () => {
    const { productId } = useParams<{ productId: string }>();
    const product = productDetailData[Number(productId)];

    const [selectedImage, setSelectedImage] = useState(product?.images[0] || '');
    const [selectedSize, setSelectedSize] = useState<string | null>(null);
    const [expandedSection, setExpandedSection] = useState<string | null>(null);

    if (!product) {
        return <div className="container-custom py-16 text-center text-xl">Product not found.</div>;
    }

    const formatPrice = (price: number) => `₹ ${price.toLocaleString()}`;

    const toggleSection = (section: string) => {
        setExpandedSection(expandedSection === section ? null : section);
    };

    const renderStars = (rating: number) => {
        return (
            <div className="flex items-center">
                {[1, 2, 3, 4, 5].map((star) => (
                    <svg
                        key={star}
                        className={`w-4 h-4 ${star <= rating ? 'text-yellow-400' : 'text-gray-300'}`}
                        fill="currentColor"
                        viewBox="0 0 20 20"
                    >
                        <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.538 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.783.57-1.838-.197-1.538-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.462a1 1 0 00.95-.69l1.07-3.292z" />
                    </svg>
                ))}
            </div>
        );
    };

    return (
        <div style={{ backgroundColor: '#F9F9F9' }} className="min-h-screen">
            {/* Header/Navigation - kept from original */}

            {/* Main Product Section */}
            <div className="max-w-6xl mx-auto px-4 py-8">
                {/* Product Images */}
                <div className="mb-8">
                    {/* Main Image */}
                    <div className="mb-4">
                        <div
                            className="w-full h-96 bg-gray-100 rounded-lg overflow-hidden"
                            style={{ maxWidth: '500px', margin: '0 auto' }}
                        >
                            <img
                                src={selectedImage}
                                alt={product.name}
                                className="w-full h-full object-cover"
                            />
                        </div>
                    </div>

                    {/* Thumbnail Images */}
                    <div className="flex gap-2 justify-center">
                        {product.images.slice(0, 5).map((img, index) => (
                            <div
                                key={index}
                                className={`w-20 h-20 bg-gray-100 rounded-lg overflow-hidden cursor-pointer border-2 ${selectedImage === img ? 'border-orange-500' : 'border-transparent'
                                    }`}
                                onClick={() => setSelectedImage(img)}
                            >
                                <img
                                    src={img}
                                    alt={`Product ${index + 1}`}
                                    className="w-full h-full object-cover"
                                />
                            </div>
                        ))}
                    </div>
                </div>

                {/* Product Information */}
                <div className="text-center mb-8">
                    {/* Product Title */}
                    <h1
                        className="text-3xl font-bold mb-4"
                        style={{
                            color: '#252525',
                            fontFamily: 'Quicksand',
                            fontWeight: 700,
                            fontSize: '32px',
                            lineHeight: '1.25em'
                        }}
                    >
                        {product.name}
                    </h1>

                    {/* Rating */}
                    <div className="flex items-center justify-center gap-2 mb-4">
                        {renderStars(product.rating)}
                        <span
                            style={{
                                color: '#8A8C8A',
                                fontFamily: 'Quicksand',
                                fontWeight: 600,
                                fontSize: '14px'
                            }}
                        >
                            ({product.reviews} ratings)
                        </span>
                    </div>

                    {/* Price Section */}
                    <div className="mb-4">
                        <div className="flex items-center justify-center gap-4 mb-2">
                            <span
                                style={{
                                    color: '#252525',
                                    fontFamily: 'Quicksand',
                                    fontWeight: 600,
                                    fontSize: '24px'
                                }}
                            >
                                {formatPrice(product.price)}
                            </span>
                            <span
                                style={{
                                    color: '#958D8D',
                                    fontFamily: 'Quicksand',
                                    fontWeight: 500,
                                    fontSize: '24px',
                                    textDecoration: 'line-through'
                                }}
                            >
                                {formatPrice(product.originalPrice)}
                            </span>
                            <span
                                style={{
                                    color: '#068743',
                                    fontFamily: 'Quicksand',
                                    fontWeight: 600,
                                    fontSize: '14px'
                                }}
                            >
                                ( {product.discount}% Off )
                            </span>
                        </div>
                        <p
                            style={{
                                color: '#8A8C8A',
                                fontFamily: 'Quicksand',
                                fontWeight: 600,
                                fontSize: '14px'
                            }}
                        >
                            Inc. of all taxes
                        </p>
                    </div>

                    {/* Shipping & Stock Info */}
                    <div className="mb-6">
                        <p
                            style={{
                                color: '#8A8C8A',
                                fontFamily: 'Quicksand',
                                fontWeight: 600,
                                fontSize: '16px'
                            }}
                        >
                            Express Shipping Available
                        </p>
                        <p
                            style={{
                                color: '#8A8C8A',
                                fontFamily: 'Quicksand',
                                fontWeight: 600,
                                fontSize: '16px'
                            }}
                        >
                            In stock , ready to ship
                        </p>
                    </div>

                    {/* Divider */}
                    <div className="border-t border-gray-400 mb-6"></div>
                </div>

                {/* Size Selection Section */}
                <div className="mb-8">
                    <div className="flex items-center justify-between mb-4">
                        <h3
                            style={{
                                color: '#000000',
                                fontFamily: 'Quicksand',
                                fontWeight: 700,
                                fontSize: '24px'
                            }}
                        >
                            Select Size
                        </h3>
                        <div className="flex items-center gap-4">
                            <button
                                style={{
                                    color: '#EB4848',
                                    fontFamily: 'Quicksand',
                                    fontWeight: 600,
                                    fontSize: '20px'
                                }}
                            >
                                Size Guide
                            </button>
                            <button
                                style={{
                                    color: '#1E1E1E',
                                    fontFamily: 'Quicksand',
                                    fontWeight: 700,
                                    fontSize: '24px'
                                }}
                            >
                                Add to Wishlist
                            </button>
                        </div>
                    </div>

                    {/* Size Options */}
                    <div className="flex justify-center gap-3 mb-4">
                        {product.sizes.map((size) => (
                            <button
                                key={size}
                                onClick={() => setSelectedSize(size)}
                                className={`w-12 h-12 rounded-full border flex items-center justify-center text-xl font-semibold transition-all ${selectedSize === size
                                        ? 'bg-gray-800 text-white border-gray-800'
                                        : 'bg-white text-gray-800 border-gray-300 hover:border-gray-400'
                                    }`}
                                style={{ borderColor: '#CBCBCB', borderWidth: '1px' }}
                            >
                                <span
                                    style={{
                                        color: selectedSize === size ? '#FFFFFF' : '#1E1E1E',
                                        fontFamily: 'Quicksand',
                                        fontWeight: 400,
                                        fontSize: '20px'
                                    }}
                                >
                                    {size}
                                </span>
                            </button>
                        ))}
                    </div>

                    {/* Stock Left */}
                    {product.stockLeft && product.stockLeft <= 5 && (
                        <div className="flex justify-center gap-8 mb-6">
                            <span
                                style={{
                                    color: '#AE3737',
                                    fontFamily: 'Quicksand',
                                    fontWeight: 400,
                                    fontSize: '12px'
                                }}
                            >
                                {product.stockLeft} Left
                            </span>
                            <span
                                style={{
                                    color: '#AE3737',
                                    fontFamily: 'Quicksand',
                                    fontWeight: 400,
                                    fontSize: '12px'
                                }}
                            >
                                {product.stockLeft} Left
                            </span>
                        </div>
                    )}

                    {/* Divider */}
                    <div className="border-t border-gray-400 mb-6"></div>

                    {/* Add to Bag Button */}
                    <div className="flex justify-center mb-8">
                        <button
                            className="px-8 py-3 rounded-lg text-white font-bold text-xl transition-colors"
                            style={{
                                backgroundColor: '#2D2D4B',
                                fontFamily: 'Quicksand',
                                fontWeight: 700,
                                fontSize: '24px'
                            }}
                        >
                            <span style={{ color: '#FFFFFF' }}>Add to Bag</span>
                        </button>
                    </div>

                    {/* Add Additional Information */}
                    <div className="text-center mb-8">
                        <div className="flex items-center justify-center gap-2">
                            <span
                                style={{
                                    color: '#FF7500',
                                    fontFamily: 'Quicksand',
                                    fontWeight: 600,
                                    fontSize: '10px'
                                }}
                            >
                                ADD ADDITIONAL INFORMATION
                            </span>
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
                                <path d="M12 5V19M5 12H19" stroke="#FF7500" strokeWidth="2" strokeLinecap="round" />
                            </svg>
                        </div>
                    </div>
                </div>

                {/* Expandable Product Information Sections */}
                <div className="mb-12">
                    {/* Description */}
                    <div className="border-b border-gray-400 py-4">
                        <button
                            className="w-full flex justify-between items-center"
                            onClick={() => toggleSection('description')}
                        >
                            <span
                                style={{
                                    color: '#000000',
                                    fontFamily: 'Quicksand',
                                    fontWeight: 700,
                                    fontSize: '20px'
                                }}
                            >
                                Description
                            </span>
                            <svg
                                className={`w-5 h-5 transform transition-transform ${expandedSection === 'description' ? 'rotate-180' : ''}`}
                                fill="none"
                                stroke="#252525"
                                viewBox="0 0 24 24"
                            >
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                            </svg>
                        </button>
                        {expandedSection === 'description' && (
                            <div className="mt-4">
                                <p
                                    style={{
                                        color: '#000000',
                                        fontFamily: 'Quicksand',
                                        fontWeight: 400,
                                        fontSize: '16px'
                                    }}
                                >
                                    {product.description}
                                </p>
                            </div>
                        )}
                    </div>

                    {/* Return & Refund Policy */}
                    <div className="border-b border-gray-400 py-4">
                        <button
                            className="w-full flex justify-between items-center"
                            onClick={() => toggleSection('returns')}
                        >
                            <span
                                style={{
                                    color: '#000000',
                                    fontFamily: 'Quicksand',
                                    fontWeight: 700,
                                    fontSize: '20px'
                                }}
                            >
                                Return & Refund Policy
                            </span>
                            <svg
                                className={`w-5 h-5 transform transition-transform ${expandedSection === 'returns' ? 'rotate-180' : ''}`}
                                fill="none"
                                stroke="#252525"
                                viewBox="0 0 24 24"
                            >
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                            </svg>
                        </button>
                        {expandedSection === 'returns' && (
                            <div className="mt-4">
                                <p
                                    style={{
                                        color: '#000000',
                                        fontFamily: 'Quicksand',
                                        fontWeight: 400,
                                        fontSize: '16px'
                                    }}
                                >
                                    {product.returnPolicy}
                                </p>
                            </div>
                        )}
                    </div>

                    {/* Care & Cleaning */}
                    <div className="border-b border-gray-400 py-4">
                        <button
                            className="w-full flex justify-between items-center"
                            onClick={() => toggleSection('care')}
                        >
                            <span
                                style={{
                                    color: '#000000',
                                    fontFamily: 'Quicksand',
                                    fontWeight: 700,
                                    fontSize: '20px'
                                }}
                            >
                                Care & Cleaning
                            </span>
                            <svg
                                className={`w-5 h-5 transform transition-transform ${expandedSection === 'care' ? 'rotate-180' : ''}`}
                                fill="none"
                                stroke="#252525"
                                viewBox="0 0 24 24"
                            >
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                            </svg>
                        </button>
                        {expandedSection === 'care' && (
                            <div className="mt-4">
                                <p
                                    style={{
                                        color: '#000000',
                                        fontFamily: 'Quicksand',
                                        fontWeight: 400,
                                        fontSize: '16px'
                                    }}
                                >
                                    {product.care}
                                </p>
                            </div>
                        )}
                    </div>

                    {/* Reviews */}
                    <div className="py-4">
                        <button
                            className="w-full flex justify-between items-center"
                            onClick={() => toggleSection('reviews')}
                        >
                            <span
                                style={{
                                    color: '#000000',
                                    fontFamily: 'Quicksand',
                                    fontWeight: 700,
                                    fontSize: '20px'
                                }}
                            >
                                Reviews
                            </span>
                            <svg
                                className={`w-5 h-5 transform transition-transform ${expandedSection === 'reviews' ? 'rotate-180' : ''}`}
                                fill="none"
                                stroke="#252525"
                                viewBox="0 0 24 24"
                            >
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                            </svg>
                        </button>
                        {expandedSection === 'reviews' && (
                            <div className="mt-4">
                                <p
                                    style={{
                                        color: '#000000',
                                        fontFamily: 'Quicksand',
                                        fontWeight: 400,
                                        fontSize: '16px'
                                    }}
                                >
                                    Customer reviews will be displayed here.
                                </p>
                            </div>
                        )}
                    </div>
                </div>

                {/* You May Also Like Section */}
                <div style={{ backgroundColor: '#131F35' }} className="rounded-lg p-8 mb-8">
                    <h2
                        className="text-center mb-8"
                        style={{
                            color: '#252525',
                            fontFamily: 'Quicksand',
                            fontWeight: 700,
                            fontSize: '40px'
                        }}
                    >
                        You May Also Like
                    </h2>

                    {/* Related Products Grid */}
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                        {relatedProducts.map((relatedProduct) => (
                            <div key={relatedProduct.id} className="relative">
                                {/* Product Image */}
                                <div className="relative bg-gray-100 rounded-lg overflow-hidden aspect-square mb-4">
                                    <Link to={`/product/${relatedProduct.id}`}>
                                        <img
                                            src={relatedProduct.image}
                                            alt={relatedProduct.name}
                                            className="w-full h-full object-cover hover:scale-105 transition-transform duration-300"
                                        />
                                    </Link>

                                    {/* Wishlist Heart */}
                                    <button className="absolute top-3 right-3 w-8 h-8 bg-white rounded-full flex items-center justify-center shadow-md">
                                        <svg className="w-4 h-4 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
                                        </svg>
                                    </button>
                                </div>

                                {/* Product Info */}
                                <div className="text-center">
                                    <Link to={`/product/${relatedProduct.id}`}>
                                        <h3
                                            className="mb-2 hover:opacity-80"
                                            style={{
                                                color: '#252525',
                                                fontFamily: 'Quicksand',
                                                fontWeight: 600,
                                                fontSize: '20px'
                                            }}
                                        >
                                            {relatedProduct.name}
                                        </h3>
                                    </Link>

                                    {/* Price */}
                                    <div className="flex items-center justify-center gap-2 mb-3">
                                        <span
                                            style={{
                                                color: '#252525',
                                                fontFamily: 'Quicksand',
                                                fontWeight: 600,
                                                fontSize: '20px'
                                            }}
                                        >
                                            {formatPrice(relatedProduct.price)}
                                        </span>
                                        <span
                                            style={{
                                                color: '#958D8D',
                                                fontFamily: 'Quicksand',
                                                fontWeight: 500,
                                                fontSize: '20px',
                                                textDecoration: 'line-through'
                                            }}
                                        >
                                            {formatPrice(relatedProduct.originalPrice)}
                                        </span>
                                        <span
                                            style={{
                                                color: '#5EC937',
                                                fontFamily: 'Quicksand',
                                                fontWeight: 600,
                                                fontSize: '12px'
                                            }}
                                        >
                                            {relatedProduct.discount}
                                        </span>
                                    </div>

                                    {/* Add to Cart Button */}
                                    <button
                                        style={{
                                            color: '#FFFFFF',
                                            fontFamily: 'Quicksand',
                                            fontWeight: 600,
                                            fontSize: '16px'
                                        }}
                                        className="w-full py-2 rounded-lg border border-white hover:bg-white hover:text-gray-800 transition-colors"
                                    >
                                        Add to Cart
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetailPage;
