import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { productAPI, cartAPI } from '../services/api';

const FeaturedProducts = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchFeaturedProducts = async () => {
            try {
                const data = await productAPI.getProducts();

                const featuredProducts = data.filter((product) => product.isFeatured === 1);
                setProducts(featuredProducts.slice(0, 4));
            } catch (error) {
            } finally {
                setLoading(false);
            }
        };

        fetchFeaturedProducts();
    }, []);

    const handleAddToCart = async (productId) => {
        try {
            await cartAPI.addToCart(productId, undefined, 1);
            alert('Product added to cart!');
        } catch (error) {
            alert('Failed to add product to cart');
        }
    };

    const formatPrice = (price) => {
        return `â‚¹${price.toLocaleString()}`;
    };

    return (
        <section className="py-8 sm:py-12 md:py-16 bg-gray-50">
            <div className="px-20">

                <div className="text-center mb-8 sm:mb-10 md:mb-12 px-4 sm:px-0">
                    <h2 className="text-2xl sm:text-3xl md:text-4xl font-bold text-dark-900 mb-4 md:mb-8">
                        Featured Products
                    </h2>
                </div>

                {/* Products Grid - Mobile optimized */}
                <div className="grid grid-cols-2 sm:grid-cols-2 lg:grid-cols-4 gap-3 sm:gap-4 md:gap-6 px-4 sm:px-0">
                    {loading ? (
                        // Loading skeleton
                        [...Array(4)].map((_, index) => (
                            <div key={index} className="bg-white rounded-lg overflow-hidden shadow-sm">
                                <div className="aspect-square bg-gray-200 animate-pulse"></div>
                                <div className="p-4">
                                    <div className="h-4 bg-gray-200 rounded animate-pulse mb-2"></div>
                                    <div className="h-4 bg-gray-200 rounded animate-pulse mb-4 w-2/3"></div>
                                    <div className="h-10 bg-gray-200 rounded animate-pulse"></div>
                                </div>
                            </div>
                        ))
                    ) : (
                        products.map((product) => (
                            // <div
                            //     key={product.id}
                            //     className="bg-white rounded-lg overflow-hidden shadow-sm hover:shadow-lg transition-shadow duration-300 group"
                            // >
                            //     {/* Product Image - Clickable */}
                            //     <Link to={`/product/${product.id}`} className="relative aspect-square overflow-hidden block">
                            //         <img
                            //             src={product.images[0] || '/images/products/placeholder.png'}
                            //             alt={product.name}
                            //             className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                            //         />


                            //         <button className="absolute top-2 sm:top-3 right-2 sm:right-3 w-6 h-6 sm:w-8 sm:h-8 bg-white rounded-full flex items-center justify-center shadow-md hover:bg-gray-50 transition-colors">
                            //             <img
                            //                 src="/images/icons/heart-icon.svg"
                            //                 alt="Add to wishlist"
                            //                 className="w-3 h-3 sm:w-4 sm:h-4"
                            //             />
                            //         </button>


                            //         {product.discount > 0 && (
                            //             <div className="absolute top-2 sm:top-3 left-2 sm:left-3 bg-success-400 text-white px-1.5 sm:px-2 py-0.5 sm:py-1 rounded text-xs sm:text-sm font-medium">
                            //                 {product.discount}% Off
                            //             </div>
                            //         )}
                            //     </Link>


                            //     <div className="p-2 sm:p-3 md:p-4">
                            //         {/* Product Name - Clickable */}
                            //         <Link to={`/product/${product.id}`}>
                            //             <h3 className="font-semibold text-dark-900 mb-1 sm:mb-2 line-clamp-2 hover:text-primary-400 transition-colors cursor-pointer text-sm sm:text-base">
                            //                 {product.name}
                            //             </h3>
                            //         </Link>


                            //         <div className="mb-2 sm:mb-3 md:mb-4">
                            //             <div className="flex items-center gap-1 sm:gap-2 flex-wrap">
                            //                 <span className="text-sm sm:text-base md:text-lg font-bold text-dark-900">
                            //                     {formatPrice(product.price)}
                            //                 </span>
                            //                 {product.originalPrice > product.price && (
                            //                     <span className="text-xs sm:text-sm text-gray-500 line-through">
                            //                         {formatPrice(product.originalPrice)}
                            //                     </span>
                            //                 )}
                            //             </div>
                            //             {product.discount > 0 && (
                            //                 <span className="text-xs sm:text-sm text-success-400 font-medium">
                            //                     ({product.discount}% Off)
                            //                 </span>
                            //             )}
                            //         </div>


                            //         <button
                            //             onClick={() => handleAddToCart(product.id)}
                            //             className="w-full bg-dark-900 text-white py-1.5 sm:py-2 px-2 sm:px-4 rounded font-medium hover:bg-dark-800 transition-colors duration-200 text-xs sm:text-sm"
                            //         >
                            //             Add to Cart
                            //         </button>
                            //     </div>
                            // </div>
                            <div
                                    key={product.id}
                                    className="flex flex-col bg-white rounded-lg overflow-hidden shadow-sm hover:shadow-lg transition-shadow duration-300 group w-[105%] h-[85%]"
                                >
                                {/* Product Image - More Height */}
                                <Link
                                    to={`/product/${product.id}`}
                                    className="relative h-[65%] overflow-hidden block"
                                >
                                    <img
                                    src={product.images[0] || '/images/products/placeholder.png'}
                                    alt={product.name}
                                    className="w-full h-full object-cover object-top group-hover:scale-105 transition-transform duration-300"
                                    />

                                    {/* Wishlist Button */}
                                    <button className="absolute top-2 sm:top-3 right-2 sm:right-3 w-6 h-6 sm:w-8 sm:h-8 bg-white rounded-full flex items-center justify-center shadow-md hover:bg-gray-50 transition-colors">
                                    <img
                                        src="/images/icons/heart-icon.svg"
                                        alt="Add to wishlist"
                                        className="w-3 h-3 sm:w-4 sm:h-4"
                                    />
                                    </button>

                                    {/* Discount Badge */}
                                    {product.discount > 0 && (
                                    <div className="absolute top-2 sm:top-3 left-2 sm:left-3 bg-success-400 text-white px-1.5 sm:px-2 py-0.5 sm:py-1 rounded text-xs sm:text-sm font-medium">
                                        {product.discount}% Off
                                    </div>
                                    )}
                                </Link>

                                {/* Product Details - Compact Layout */}
                                <div className="p-3 sm:p-4 flex flex-col flex-1">
                                    {/* Product Name */}
                                    <Link to={`/product/${product.id}`}>
                                        <h3 className="font-semibold text-dark-900 mb-1 line-clamp-2 hover:text-primary-400 transition-colors cursor-pointer text-sm sm:text-base text-left">
                                        {product.name}
                                        </h3>
                                    </Link>

                                    {/* Price Section */}
                                    <div className="text-left mb-2">
                                        <div className="flex items-center gap-2 flex-wrap">
                                        <span className="text-sm sm:text-base md:text-lg font-bold text-dark-900">
                                            {formatPrice(product.price)}
                                        </span>
                                        {product.originalPrice > product.price && (
                                            <span className="text-xs sm:text-sm text-gray-500 line-through">
                                            {formatPrice(product.originalPrice)}
                                            </span>
                                        )}
                                        </div>
                                        {product.discount > 0 && (
                                        <span className="text-xs sm:text-sm text-success-400 font-medium">
                                            ({product.discount}% Off)
                                        </span>
                                        )}
                                    </div>

                                    {/* Button Always at Bottom */}
                                    <button
                                    onClick={() => handleAddToCart(product.id)}
                                    className="w-full bg-dark-900 text-white py-2 px-4 rounded font-medium hover:bg-dark-800 transition-colors duration-200 text-sm mt-auto"
                                    >
                                    Add to Cart
                                    </button>
                                </div>
                             </div>
                        ))
                    )}
                </div>


                <div className="text-center mt-8 sm:mt-10 md:mt-12 px-4 sm:px-0">
                    <Link to="/listing" className="btn-secondary px-6 sm:px-8 py-2.5 sm:py-3 text-sm sm:text-base">
                        View All Products
                    </Link>
                </div>
            </div>
        </section>
    );
};

export default FeaturedProducts;
