import React from 'react';

interface Category {
    id: number;
    name: string;
    image: string;
}

const CategoriesSection: React.FC = () => {
    const categories: Category[] = [
        {
            id: 1,
            name: "Pashmina Shawls",
            image: "/images/categories/category-pashmina-shawls.png"
        },
        {
            id: 2,
            name: "Pashmina Stoles",
            image: "/images/categories/category-pashmina-stoles.png"
        },
        {
            id: 3,
            name: "Capes",
            image: "/images/categories/category-capes.png"
        }
    ];

    return (
        <section className="py-16 bg-white">
            <div className="container-custom">
                {/* Section Header */}
                <div className="text-center mb-12">
                    <h2 className="text-3xl md:text-4xl font-bold text-dark-900 mb-4">
                        Explore Our New Collection
                    </h2>
                    <div className="flex justify-center gap-4 mb-8">
                        <button className="btn-secondary text-sm">
                            Our Products
                        </button>
                    </div>
                </div>

                {/* Categories Grid */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                    {categories.map((category) => (
                        <div
                            key={category.id}
                            className="group relative overflow-hidden rounded-2xl bg-gray-100 aspect-square cursor-pointer transition-transform duration-300 hover:scale-105"
                        >
                            {/* Category Image */}
                            <div className="absolute inset-0">
                                <img
                                    src={category.image}
                                    alt={category.name}
                                    className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-110"
                                />
                                {/* Gradient Overlay */}
                                <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent"></div>
                            </div>

                            {/* Category Content */}
                            <div className="absolute bottom-6 left-6 right-6 text-white">
                                <h3 className="text-xl font-bold mb-3">
                                    {category.name}
                                </h3>
                                <button className="btn-primary text-sm px-6 py-2">
                                    Shop Now
                                </button>
                            </div>
                        </div>
                    ))}
                </div>

                {/* Bottom Section - Corporate Gifting */}
                <div className="mt-16 text-center">
                    <h3 className="text-2xl md:text-3xl font-bold text-dark-900 mb-8">
                        Explore Our Corporate Gifting Options
                    </h3>

                    {/* Corporate Gifting Card */}
                    <div
                        className="relative rounded-2xl overflow-hidden max-w-4xl mx-auto aspect-[2/1] bg-gray-200"
                        style={{
                            backgroundImage: 'url("/images/banners/promotional-banner.png")',
                            backgroundSize: 'cover',
                            backgroundPosition: 'center'
                        }}
                    >
                        {/* Overlay */}
                        <div className="absolute inset-0 bg-black bg-opacity-30"></div>

                        {/* Content */}
                        <div className="absolute inset-0 flex items-center justify-center text-white">
                            <div className="text-center">
                                <h4 className="text-2xl md:text-3xl font-bold mb-4">
                                    Gifts That Speak From the Heart
                                </h4>
                                <button className="btn-primary">
                                    Explore More
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default CategoriesSection;
