import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const HeroSection = () => {
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    const backgroundImages = [
        "/images/banners/hero-background.png",
        "/images/banners/promotional-banner.png",
        "/images/banners/promotioanl-banner-2.png"
    ];

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentImageIndex((prevIndex) => 
                (prevIndex + 1) % backgroundImages.length
            );
        }, 5000); // Change image every 5 seconds

        return () => clearInterval(interval);
    }, []);

    return (
        <section
            className="relative min-h-screen md:min-h-screen flex items-center justify-center text-white overflow-hidden"
        >
            {/* Background Images */}
            {backgroundImages.map((image, index) => (
                <div
                    key={index}
                    className={`absolute inset-0 transition-opacity duration-1000 ${
                        index === currentImageIndex ? 'opacity-100' : 'opacity-0'
                    }`}
                    style={{
                        backgroundImage: `url("${image}")`,
                        backgroundSize: 'cover',
                        backgroundPosition: 'center',
                        backgroundRepeat: 'no-repeat'
                    }}
                />
            ))}

            {/* Dark overlay */}
            <div className="absolute inset-0 bg-black bg-opacity-40"></div>

            {/* Content */}
            <div className="relative z-10 container-custom text-center px-4 md:px-6">
                <div className="max-w-4xl mx-auto">
                    {/* Main Heading - More mobile responsive */}
                    <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-bold mb-4 md:mb-6 leading-tight">
                        "Picture-Perfect Moments,
                    </h1>

                    {/* Subheading - Better mobile scaling */}
                    <h2 className="text-xl sm:text-2xl md:text-3xl font-bold mb-3 md:mb-4 text-primary-400">
                        Thoughtfully Curated Gifts for Every Occasion
                    </h2>

                    {/* Description - More compact on mobile */}
                    <p className="text-base sm:text-lg md:text-xl mb-6 md:mb-8 max-w-2xl mx-auto leading-relaxed px-2">
                        Explore our diverse range of gift options tailored for every special moment.
                    </p>

                    {/* CTA Button - More mobile friendly */}
                    <Link to="/listing" className="btn-primary text-base sm:text-lg px-6 sm:px-8 py-3 sm:py-4 mb-8 md:mb-16 inline-block">
                        Shop Now
                    </Link>
                </div>
            </div>

            {/* Image Indicators */}
            <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 z-20 flex space-x-3">
                {backgroundImages.map((_, index) => (
                    <button
                        key={index}
                        onClick={() => setCurrentImageIndex(index)}
                        className={`w-3 h-3 rounded-full transition-all duration-300 ${
                            index === currentImageIndex 
                                ? 'bg-white scale-125' 
                                : 'bg-white/50 hover:bg-white/75'
                        }`}
                        aria-label={`Go to image ${index + 1}`}
                    />
                ))}
            </div>
        </section>
    );
};

export default HeroSection;
