import React, { useMemo, useState } from "react";

const clamp = (v, lo, hi) => Math.min(Math.max(v, lo), hi);

const FilterModal = ({
  isOpen,
  onClose,
  filters,
  onApplyFilters,
  onClearFilters,
}) => {
  const [tempFilters, setTempFilters] = useState(filters);

  // screenshot bounds
  const ABS_MIN = 200;
  const ABS_MAX = 25000;
  const STEP = 100;

  // dual-thumb slider values (kept ordered) - must be before early return
  const { minVal, maxVal } = useMemo(() => {
    const min = clamp(tempFilters.priceRange.min, ABS_MIN, ABS_MAX);
    const max = clamp(tempFilters.priceRange.max, min, ABS_MAX);
    return { minVal: min, maxVal: max };
  }, [tempFilters]);

  if (!isOpen) return null;

  // --------- price helpers ---------
  const setPrice = (field, value) => {
    const num = Number.parseInt(value || 0, 10);
    setTempFilters((prev) => {
      const next = { ...prev, priceRange: { ...prev.priceRange } };
      if (field === "min") {
        next.priceRange.min = clamp(num, ABS_MIN, Math.min(prev.priceRange.max, ABS_MAX));
      } else {
        next.priceRange.max = clamp(num, Math.max(prev.priceRange.min, ABS_MIN), ABS_MAX);
      }
      return next;
    });
  };

  const percentFromValue = (v) =>
    ((v - ABS_MIN) / (ABS_MAX - ABS_MIN)) * 100;

  // --------- actions ---------
  const handleApply = () => onApplyFilters?.(tempFilters);

  const handleClear = () => {
    const cleared = {
      priceRange: { min: ABS_MIN, max: ABS_MAX },
      categories: [],
      colors: [],
      sizes: [],
      discount: false,
      inStock: false,
    };
    setTempFilters(cleared);
    onClearFilters?.();
  };

  return (
    <div className="fixed inset-0 z-50 overflow-hidden">
      {/* Backdrop */}
      <div
        className="fixed inset-0 bg-black/50"
        onClick={onClose}
        aria-hidden="true"
      />

      {/* Sheet: keep EXACT same height as your original */}
      <div className="fixed inset-x-0 bottom-0 sm:left-0 sm:top-0 sm:left-auto h-[25vh] sm:h-[85vh] w-full sm:w-96 bg-white shadow-xl transform transition-transform rounded-t-3xl sm:rounded-none flex flex-col">
        {/* Header */}
        <div className="relative flex items-center justify-between px-6 pt-5 pb-3 border-b">
          <div className="w-10 h-1 bg-gray-300 rounded-full sm:hidden absolute top-2 left-1/2 -translate-x-1/2" />
          <h2 className="text-[28px] leading-8 font-semibold text-[#121212]">Filters</h2>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-full"
