import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { categoryAPI } from '../services/api';

const CategoriesSection = () => {
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const data = await categoryAPI.getCategories();
                setCategories(data);
            } catch (error) {
            } finally {
                setLoading(false);
            }
        };

        fetchCategories();
    }, []);

    return (
        <section className="py-8 sm:py-12 md:py-16 bg-white">
            <div className="p-24">

            <h2 className="text-xl sm:text-2xl md:text-3xl font-bold mb-3 md:mb-4 text-black pb-10">
                        "Thoughtfully Curated Gifts for Every Occasion"
                    </h2>



                {/* Categories Grid - Mobile optimized */}
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 sm:gap-6 md:gap-8 px-4 sm:px-0">
                    {loading ? (
                        // Loading skeleton
                        [...Array(3)].map((_, index) => (
                            <div key={index} className="aspect-square bg-gray-200 rounded-xl sm:rounded-2xl animate-pulse"></div>
                        ))
                    ) : (
                        categories.map((category) => (
                            <Link
                                key={category.id}
                                to={`/listing?category=${category.slug}`}
                                className="group relative overflow-hidden rounded-xl sm:rounded-2xl bg-gray-100 aspect-square cursor-pointer transition-transform duration-300 hover:scale-105 block"
                            >
                                {/* Category Image */}
                                <div className="absolute inset-0">
                                    <img
                                        src={category.image}
                                        alt={category.name}
                                        className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-110"
                                    />
                                    {/* Gradient Overlay */}
                                    <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent"></div>
                                </div>


                                <div className="absolute inset-0 flex items-center justify-center text-white">
                                    <h3 className="text-xl sm:text-2xl md:text-3xl font-bold text-center px-4">
                                        {category.name}
                                    </h3>
                                </div>
                            </Link>
                        ))
                    )}
                </div>

                {/* Bottom Section - Corporate Gifting */}

                <div className="text-center pt-24">
                <div className="flex justify-center gap-2 mb-4 md:mb-8">
                        <Link to="/listing" className="btn-secondary text-sm px-2 ">
                            Our Products
                        </Link>
                    </div>
                    <h2 className="text-2xl sm:text-3xl md:text-4xl font-bold text-dark-900 mb-4 px-4">
                        Explore Our New Collection
                    </h2>

                </div>


                <div className="mt-10 sm:mt-12 md:mt-16 text-center px-4 sm:px-0">
                    <h3 className="text-xl sm:text-2xl md:text-3xl font-bold text-dark-900 mb-6 md:mb-8">
                        Explore Our Corporate Gifting Options
                    </h3>
                </div>
            </div>

            {/* Full-Width Corporate Gifting Section - Mobile Responsive */}
            <div className="relative left-1/2 right-1/2 -ml-[50vw] -mr-[50vw] w-screen">
                <div
                    className="relative w-full bg-gray-200"
                    style={{
                        backgroundImage: 'url("/images/banners/promotional-banner.png")',
                        backgroundSize: 'cover',
                        backgroundPosition: 'center',
                        backgroundRepeat: 'no-repeat',
                        height: '300px',
                        minHeight: '280px'
                    }}
                >
                    {/* Overlay */}
                    <div className="absolute inset-0 bg-black bg-opacity-50"></div>


                    <div className="absolute inset-0 flex items-center justify-center text-white">
                        <div className="text-center px-4 sm:px-6">
                            <h4 className="text-xl sm:text-2xl md:text-3xl font-bold mb-4 sm:mb-6 leading-tight text-white">
                                Gifts That Speak From the Heart
                            </h4>
                            <Link
                                to="/listing"
                                className="inline-block px-6 sm:px-8 py-2.5 sm:py-3 rounded-lg font-semibold transition-all hover:opacity-90 text-sm sm:text-base md:text-lg"
                                style={{
                                    backgroundColor: '#FF7500',
                                    color: '#FFFFFF'
                                }}
                            >
                                Explore More
                            </Link>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default CategoriesSection;
