import React, { useState } from 'react';

const FilterModal = ({
    isOpen,
    onClose,
    filters,
    onApplyFilters,
    onClearFilters,
}) => {
    const [tempFilters, setTempFilters] = useState(filters);

    if (!isOpen) return null;

    const handlePriceChange = (field, value) => {
        const numValue = parseInt(value) || 0;
        setTempFilters(prev => ({
            ...prev,
            priceRange: {
                ...prev.priceRange,
                [field]: numValue
            }
        }));
    };

    const handleCategoryToggle = (category) => {
        setTempFilters(prev => ({
            ...prev,
            categories: prev.categories.includes(category)
                ? prev.categories.filter(c => c !== category)
                : [...prev.categories, category]
        }));
    };

    const handleColorToggle = (color) => {
        setTempFilters(prev => ({
            ...prev,
            colors: prev.colors.includes(color)
                ? prev.colors.filter(c => c !== color)
                : [...prev.colors, color]
        }));
    };

    const handleSizeToggle = (size) => {
        setTempFilters(prev => ({
            ...prev,
            sizes: prev.sizes.includes(size)
                ? prev.sizes.filter(s => s !== size)
                : [...prev.sizes, size]
        }));
    };

    const handleApply = () => {
        onApplyFilters(tempFilters);
    };

    const handleClear = () => {
        const clearedFilters = {
            priceRange: { min: 0, max: 50000 },
            categories: [],
            colors: [],
            sizes: [],
            discount: false,
            inStock: false,
        };
        setTempFilters(clearedFilters);
        onClearFilters();
    };

    return (
        <div className="fixed inset-0 z-50 overflow-hidden">
            {/* Backdrop */}
            <div
                className="fixed inset-0 bg-black bg-opacity-50 transition-opacity"
                onClick={onClose}
            />


            <div className="fixed inset-x-0 bottom-0 sm:left-0 sm:top-0 sm:left-auto h-[25vh] sm:h-[25vh] w-full sm:w-96 bg-white shadow-xl transform transition-transform rounded-t-3xl sm:rounded-none">

                <div className="flex items-center justify-between p-4 sm:p-6 border-b">
                    {/* Mobile handle */}
                    <div className="w-10 h-1 bg-gray-300 rounded-full sm:hidden absolute top-2 left-1/2 transform -translate-x-1/2"></div>
                    <h2 className="text-lg sm:text-xl font-bold text-gray-900">Filters</h2>
                    <button
                        onClick={onClose}
                        className="p-2 hover:bg-gray-100 rounded-full transition-colors"
                    >
                        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                        </svg>
                    </button>
                </div>


                <div className="flex-1 overflow-y-auto p-4 sm:p-6 space-y-6 sm:space-y-8">

                    <div>
                        <h3 className="text-base sm:text-lg font-semibold text-gray-900 mb-3 sm:mb-4">Price</h3>
                        <div className="space-y-3 sm:space-y-4">
                            <div className="flex gap-3 sm:gap-4">
                                <div className="flex-1">
                                    <label className="block text-xs sm:text-sm text-gray-600 mb-1">Min. Amount</label>
                                    <input
                                        type="number"
                                        value={tempFilters.priceRange.min}
                                        onChange={(e) => handlePriceChange('min', e.target.value)}
                                        className="w-full px-2 sm:px-3 py-2 text-sm border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-400"
                                        placeholder="200"
                                    />
                                </div>
                                <div className="flex-1">
                                    <label className="block text-xs sm:text-sm text-gray-600 mb-1">Max. Amount</label>
                                    <input
                                        type="number"
                                        value={tempFilters.priceRange.max}
                                        onChange={(e) => handlePriceChange('max', e.target.value)}
                                        className="w-full px-2 sm:px-3 py-2 text-sm border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-400"
                                        placeholder="25,000"
                                    />
                                </div>
                            </div>

                            {/* Price Range Slider */}
                            <div className="relative">
                                <input
                                    type="range"
                                    min="200"
                                    max="25000"
                                    step="100"
                                    value={tempFilters.priceRange.max}
                                    onChange={(e) => handlePriceChange('max', e.target.value)}
                                    className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                                />
                                <div className="flex justify-between text-xs text-gray-500 mt-1">
                                    <span>₹{tempFilters.priceRange.min.toLocaleString()}</span>
                                    <span>₹{tempFilters.priceRange.max.toLocaleString()}</span>
                                </div>
                            </div>
                        </div>
                    </div>


                    <div>
                        <h3 className="text-base sm:text-lg font-semibold text-gray-900 mb-3 sm:mb-4">Category</h3>
                        <div className="space-y-2 sm:space-y-3">
                            {['Pashmina Shawls', 'Pashmina Stoles', 'Capes'].map((category) => (
                                <label key={category} className="flex items-center">
                                    <input
                                        type="checkbox"
                                        checked={tempFilters.categories.includes(category)}
                                        onChange={() => handleCategoryToggle(category)}
                                        className="w-4 h-4 text-primary-400 bg-gray-100 border-gray-300 rounded focus:ring-primary-400 focus:ring-2"
                                    />
                                    <span className="ml-2 sm:ml-3 text-sm text-gray-700">{category}</span>
                                </label>
                            ))}
                        </div>
                    </div>


                    <div>
                        <h3 className="text-base sm:text-lg font-semibold text-gray-900 mb-3 sm:mb-4">Color</h3>
                        <div className="space-y-2 sm:space-y-3">
                            {['Red', 'Blue', 'Green', 'Yellow', 'Pink', 'Purple'].map((color) => (
                                <label key={color} className="flex items-center">
                                    <input
                                        type="checkbox"
                                        checked={tempFilters.colors.includes(color)}
                                        onChange={() => handleColorToggle(color)}
                                        className="w-4 h-4 text-primary-400 bg-gray-100 border-gray-300 rounded focus:ring-primary-400 focus:ring-2"
                                    />
                                    <span className="ml-2 sm:ml-3 text-sm text-gray-700">{color}</span>
                                </label>
                            ))}
                        </div>
                    </div>


                    <div>
                        <h3 className="text-base sm:text-lg font-semibold text-gray-900 mb-3 sm:mb-4">Product Type</h3>
                        <div className="space-y-2 sm:space-y-3">
                            {['S', 'M', 'L', 'XL', '2XL'].map((size) => (
                                <label key={size} className="flex items-center">
                                    <input
                                        type="checkbox"
                                        checked={tempFilters.sizes.includes(size)}
                                        onChange={() => handleSizeToggle(size)}
                                        className="w-4 h-4 text-primary-400 bg-gray-100 border-gray-300 rounded focus:ring-primary-400 focus:ring-2"
                                    />
                                    <span className="ml-2 sm:ml-3 text-sm text-gray-700">Size {size}</span>
                                </label>
                            ))}
                        </div>
                    </div>


                    <div className="space-y-3 sm:space-y-4">
                        <label className="flex items-center">
                            <input
                                type="checkbox"
                                checked={tempFilters.discount}
                                onChange={(e) => setTempFilters(prev => ({ ...prev, discount: e.target.checked }))}
                                className="w-4 h-4 text-primary-400 bg-gray-100 border-gray-300 rounded focus:ring-primary-400 focus:ring-2"
                            />
                            <span className="ml-2 sm:ml-3 text-sm text-gray-700">On Sale</span>
                        </label>

                        <label className="flex items-center">
                            <input
                                type="checkbox"
                                checked={tempFilters.inStock}
                                onChange={(e) => setTempFilters(prev => ({ ...prev, inStock: e.target.checked }))}
                                className="w-4 h-4 text-primary-400 bg-gray-100 border-gray-300 rounded focus:ring-primary-400 focus:ring-2"
                            />
                            <span className="ml-2 sm:ml-3 text-sm text-gray-700">In Stock</span>
                        </label>
                    </div>
                </div>


                <div className="border-t p-4 sm:p-6 space-y-2 sm:space-y-3 bg-white">
                    <button
                        onClick={handleApply}
                        className="w-full bg-primary-400 text-white font-semibold py-3 sm:py-3 px-4 rounded-md hover:bg-primary-500 transition-colors text-sm sm:text-base"
                    >
                        Apply Filters
                    </button>
                    <button
                        onClick={handleClear}
                        className="w-full bg-gray-100 text-gray-700 font-semibold py-3 sm:py-3 px-4 rounded-md hover:bg-gray-200 transition-colors text-sm sm:text-base"
                    >
                        Clear All
                    </button>
                </div>
            </div>
        </div>
    );
};

export default FilterModal;
