import React, { useState } from 'react';

const NewsletterSection: React.FC = () => {
    const [email, setEmail] = useState('');
    const [isSubmitted, setIsSubmitted] = useState(false);

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (email) {
            // Here you would typically send the email to your backend
            console.log('Newsletter signup:', email);
            setIsSubmitted(true);
            setTimeout(() => {
                setIsSubmitted(false);
                setEmail('');
            }, 3000);
        }
    };

    return (
        <section className="py-16 bg-primary-400">
            <div className="container-custom">
                <div className="max-w-4xl mx-auto text-center text-white">
                    {/* Header */}
                    <h2 className="text-3xl md:text-4xl font-bold mb-4">
                        Let's Unbox a dream together!
                    </h2>

                    {/* Description */}
                    <p className="text-lg mb-8 opacity-90">
                        Get all the latest information on Events, Sales and Offers.
                        Sign up for the newsletter today.
                    </p>

                    {/* Newsletter Form */}
                    <div className="max-w-md mx-auto">
                        {!isSubmitted ? (
                            <form onSubmit={handleSubmit} className="flex gap-2">
                                <div className="flex-1">
                                    <input
                                        type="email"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                        placeholder="Enter your email address"
                                        className="w-full px-4 py-3 rounded-lg border border-white/20 bg-white/10 text-white placeholder-white/70 focus:outline-none focus:ring-2 focus:ring-white/50 backdrop-blur-sm"
                                        required
                                    />
                                </div>
                                <button
                                    type="submit"
                                    className="px-6 py-3 bg-white text-primary-400 font-semibold rounded-lg hover:bg-gray-100 transition-colors duration-200 flex items-center"
                                    aria-label="Subscribe to newsletter"
                                >
                                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                                    </svg>
                                </button>
                            </form>
                        ) : (
                            <div className="bg-white/10 backdrop-blur-sm rounded-lg p-4 border border-white/20">
                                <div className="flex items-center justify-center gap-2 text-white">
                                    <svg className="w-6 h-6 text-green-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                                    </svg>
                                    <span className="font-medium">Thank you for subscribing!</span>
                                </div>
                                <p className="text-sm mt-2 opacity-90">
                                    You'll receive our latest updates and exclusive offers.
                                </p>
                            </div>
                        )}
                    </div>

                    {/* Privacy Notice */}
                    <p className="text-sm mt-6 opacity-70">
                        We respect your privacy. Unsubscribe at any time.
                    </p>

                    {/* Decorative Elements */}
                    <div className="flex justify-center space-x-8 mt-8 opacity-60">
                        <div className="w-16 h-0.5 bg-white"></div>
                        <div className="w-2 h-2 bg-white rounded-full mt-[-3px]"></div>
                        <div className="w-16 h-0.5 bg-white"></div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default NewsletterSection;
