import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { productAPI, cartAPI, Product, CartItem, CartResponse } from '../services/api';

// Product Detail Interface
interface ProductDetail {
    id: number;
    name: string;
    price: number;
    originalPrice: number;
    discount: number;
    rating: number;
    reviews: number;
    sizes: string[];
    inStock: boolean;
    stockLeft?: number;
    images: string[];
    description: string;
    care: string;
    returnPolicy: string;
}

// Related Product Interface
interface RelatedProduct {
    id: number;
    name: string;
    price: number;
    originalPrice: number;
    discount: string;
    image: string;
}

// Mock product detail data
const productDetailData: { [key: number]: ProductDetail } = {
    1: {
        id: 1,
        name: 'Handwoven Pashmina multicolor shawl in dual tone',
        price: 4999,
        originalPrice: 8999,
        discount: 50,
        rating: 3.0,
        reviews: 42,
        sizes: ['S', 'M', 'L', 'XL', '2XL'],
        inStock: true,
        stockLeft: 3,
        images: [
            '/images/products/product-1.png',
            '/images/products/product-2.png',
            '/images/products/product-3.png',
            '/images/products/product-4.png',
            '/images/products/product-1.png',
        ],
        description: 'Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus. Donec quam felis, ultricies nec, pellentesque eu, pretium quis, sem. Nulla consequat massa quis enim.',
        care: 'Dry clean only. Store in a cool, dry place. Avoid direct sunlight.',
        returnPolicy: '30-day easy returns and exchanges. Free return shipping.',
    },
};

// Related products
const relatedProducts: RelatedProduct[] = [
    {
        id: 2,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '( 50 % Off )',
        image: '/images/products/product-2.png',
    },
    {
        id: 3,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '( 50 % Off )',
        image: '/images/products/product-3.png',
    },
    {
        id: 4,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '( 50 % Off )',
        image: '/images/products/product-4.png',
    },
    {
        id: 5,
        name: 'Multicolor Shawl with embroidery',
        price: 4999,
        originalPrice: 8999,
        discount: '( 50 % Off )',
        image: '/images/products/product-1.png',
    },
];

const ProductDetailPage: React.FC = () => {
  const { productId } = useParams<{ productId: string }>();
  const [product, setProduct] = useState<Product | null>(null);
  const [relatedProducts, setRelatedProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

    const [selectedImage, setSelectedImage] = useState(product?.images[0] || '');
    const [selectedSize, setSelectedSize] = useState<string | null>(null);
    const [expandedSection, setExpandedSection] = useState<string | null>(null);
    const [isCartOpen, setIsCartOpen] = useState(false);
