      // Refresh cart data
      const updatedCart = await cartAPI.getCart();
      setCartData(updatedCart);
      setIsCartOpen(true);
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Failed to add item to cart');
    }
  };

